void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);
    if(p.x < N.x && p.y < N.y)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch1, pos);

        float id = pos.x + float(N.x)*pos.y;
        //neighbors
   		get(ivec2(U.xy));

        vec2 F = vec2(0.,0.);

        vec4 p0 = getParticle(ids[0]);
        for(int i = 1; i < CN; i++)
        {
            vec4 pi = getParticle(ids[i]);
        	F += Fv(U.xy, pi.xy);    
        }

        if(iMouse.z > 0.) 
        {
            float d = distance(iMouse.xy, U.xy);
            F += 3.*normalize(iMouse.xy - U.xy)/(sqrt(d)+2.);
        }

        F += 0.05*(hash21(id+3.2533*iTime) - 0.5)*length(U.zw);

        U.zw += dt*F;

        U.xy += U.zw*dt*(pow(hash11(float(ids[0])),2.));

        //border conditions
        U.xy = modc(U.xy, size);

        //this pixel value
        if(iFrame<3)
        {
            U.xy = size*hash22(pos)+1.;
			U.zw = 12.*hash22(5.14159*pos)*(hash22(3.14159*pos) - vec2(0.5, 0.7));
        }
    }
    else discard;
}

