//map distribution functions to texture coordinates
//4 texels are used to store the 9 distribution functions in one cell
#define f0(x,y) texture(iChannel0, (vec2(2*x,2*y)+0.5)/iResolution.xy).r;
#define f1(x,y) texture(iChannel0, (vec2(2*x,2*y)+0.5)/iResolution.xy).g;
#define f2(x,y) texture(iChannel0, (vec2(2*x,2*y)+0.5)/iResolution.xy).b;
#define f3(x,y) texture(iChannel0, (vec2(2*x+1,2*y)+0.5)/iResolution.xy).r;
#define f4(x,y) texture(iChannel0, (vec2(2*x+1,2*y)+0.5)/iResolution.xy).g;
#define f5(x,y) texture(iChannel0, (vec2(2*x+1,2*y)+0.5)/iResolution.xy).b;
#define f6(x,y) texture(iChannel0, (vec2(2*x,2*y+1)+0.5)/iResolution.xy).r;
#define f7(x,y) texture(iChannel0, (vec2(2*x,2*y+1)+0.5)/iResolution.xy).g;
#define f8(x,y) texture(iChannel0, (vec2(2*x,2*y+1)+0.5)/iResolution.xy).b;

#define G 0.5
#define w 0.9
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int LatSizeX = int(iResolution.y/2.0);
    int LatSizeY = int(iResolution.y/2.0);

    int ix = int(floor(fragCoord.x/2.0));
    int iy = int(floor(fragCoord.y/2.0));

    if( ix >= LatSizeX || iy >= LatSizeY )
    {
        return;
    }    
    int itx = int(fragCoord.x) - 2*ix;
    int ity = int(fragCoord.y) - 2*iy;

    float f0,f1,f2,f3,f4,f5,f6,f7,f8; 
    float rho, vx, vy;
    if((iFrame==0))
    {
        rho = 1.0;
        vx = vy = 0.0;
        f0 = 1.0-5.0/6.0*G;
        f1 = 1.0/6.0 *G;
        f2 = 1.0/6.0 *G;

        f3 = 1.0/6.0 *G;
        f4 = 1.0/6.0 *G;
        f5 = 1.0/24.0*G;

        f6 = 1.0/24.0*G;
        f7 = 1.0/24.0*G;
        f8 = 1.0/24.0*G;
    }
    else
    {
        int xplus  = clamp(ix+1,0,LatSizeX-1);
        int xminus = clamp(ix-1,0,LatSizeX-1);
        int yplus  = clamp(iy+1,0,LatSizeY-1);
        int yminus = clamp(iy-1,0,LatSizeY-1);

        f0 = f0( ix    ,iy    );
        f1 = f1( xminus,iy    );
        f2 = f2( xplus ,iy    );

        f3 = f3( ix    ,yminus);
        f4 = f4( ix    ,yplus );
        f5 = f5( xminus,yminus);

        f6 = f6( xplus ,yplus );
        f7 = f7( xplus ,yminus);
        f8 = f8( xminus,yplus );

        rho = f0+f1+f2+f3+f4+f5+f6+f7+f8;
        vx = (1.0/rho)*(f1-f2+f5-f6-f7+f8);
        vy = (1.0/rho)*(f3-f4+f5-f6+f7-f8);  


        vx = clamp(vx,-0.999,0.999);
        vy = clamp(vy,-0.999,0.999);
        //rho = mix(rho,1.0,0.01);
        rho = rho*0.999;
        if(ix==0||ix==LatSizeX-1)//boundary condition
        {
            vx = -vx;
        }
        if(iy==0||iy==LatSizeY-1)//boundary condition
        {
            vy = -vy;
        }
        rho += texture(iChannel1, (fragCoord)/iResolution.xy).x;
        rho = clamp(rho,1.0,1.5);        

        float sq_term = (vx*vx+vy*vy);

        float f0eq = rho*(1.0-10.0/12.0*G*rho-2.0/3.0*sq_term);
        float f1eq = rho*(1.0/6.0 *G*rho+1.0/3.0 *vx     +vx*vx/2.0            -sq_term/6.0);
        float f2eq = rho*(1.0/6.0 *G*rho-1.0/3.0 *vx     +vx*vx/2.0            -sq_term/6.0);

        float f3eq = rho*(1.0/6.0 *G*rho+1.0/3.0 *vy     +vy*vy/2.0            -sq_term/6.0);
        float f4eq = rho*(1.0/6.0 *G*rho-1.0/3.0 *vy     +vy*vy/2.0            -sq_term/6.0);
        float f5eq = rho*(1.0/24.0*G*rho+1.0/12.0*(vx+vy)+1.0/8.0*(vx+vy)*(vx+vy)-sq_term/24.0);  

        float f6eq = rho*(1.0/24.0*G*rho-1.0/12.0*(vx+vy)+1.0/8.0*(vx+vy)*(vx+vy)-sq_term/24.0);
        float f7eq = rho*(1.0/24.0*G*rho-1.0/12.0*(vx-vy)+1.0/8.0*(vx-vy)*(vx-vy)-sq_term/24.0);
        float f8eq = rho*(1.0/24.0*G*rho+1.0/12.0*(vx-vy)+1.0/8.0*(vx-vy)*(vx-vy)-sq_term/24.0);
        //=== RELAX TOWARD EQUILIBRIUM ========================
        f0 = mix(f0,f0eq,w);
        f1 = mix(f1,f1eq,w);
        f2 = mix(f2,f2eq,w);
        f3 = mix(f3,f3eq,w);
        f4 = mix(f4,f4eq,w);
        f5 = mix(f5,f5eq,w);
        f6 = mix(f6,f6eq,w);
        f7 = mix(f7,f7eq,w);
        f8 = mix(f8,f8eq,w);
    }
    if(itx==0&&ity==0)
    {
        fragColor.rgb = min(abs(vec3(f0,f1,f2)),1.0);
    }
    else if(itx==1&&ity==0)
    {
        fragColor.rgb = min(abs(vec3(f3,f4,f5)),1.0);
    }
    else if(itx==0&&ity==1)
    {
        fragColor.rgb = min(abs(vec3(f6,f7,f8)),1.0);
    }
    else
    {
        fragColor.rgb = min(abs(vec3(0.5*rho,0.5*vx+0.5,0.5*vy+0.5)),1.0);
    }

}

