vec4 airplay(vec3 p){
  p.x +=8.;
  p.z -= 2.2;
  p.y += 0.7;
  p.xz *= rot(0.9);
  float s = 1.3;
  p *= s;
  vec3 pp = p;
  p.y += 0.35;
  float d1 = cube(p+vec3(0.0,1.,0.0),vec3(3.7,1.6,2.9));
  for(int i = 0;i<4;i++){
    p = abs(p)-vec3(0.6,-0.13,0.4);
    p.xz *= rot(1.5);
    p.xy *= rot(1.4);
  }
    float d = cube(p,vec3(0.13,0.9,1.9));
    d = max(d,-d1);
    p = pp;
    float dk1 = cube(p+vec3(0.0,1.4,0.0),vec3(3.7,1.6,2.9));
    p.y -= 0.5;
    p.x = abs(p.x)-0.4;
    p.xy = pmod(p.xy,6.);
      for(int i = 0;i<4;i++){
        p = abs(p)-0.4*vec3(0.2,0.,0.2);
        p.xz *= rot(0.1);
        p.xy *= rot(1.1);
      }
      float d2 = cube(p,vec3(0.6,0.4,1.9));
      d2 = max(d2,-dk1);
      d = min(d,d2);
      p = pp;
      p.xz = abs(p.xz)-0.9;
      p.yz *= rot(1.);
      p.xz *= rot(-0.4);
      float d3 = cube(p,vec3(0.05,0.05,1.5));
      d = min(d,d3);
      p = pp;
      p.x = abs(p.x)-0.8;
      p.z -= 1.3;
      p.y -= 0.9;
      p.xy *= rot(0.48);
      p.xz *= rot(0.3);
      p.yz  *= rot(0.2);
      float d4 = cube(p,vec3(0.04,0.18,0.7));
      d = max(d,-d4);
      vec3 mat  = vec3(1.0);

      mat = mix(0.3*vec3(0.5,0.9,0.9),mat,step(0.01,d4));
      p = pp;
      p.z += 2.;
      p.y -= 1.2;
        float d6 =  cube(p+vec3(0.,0.,1.),vec3(0.2,2.,1.));
      p.yz *= rot(1.57*0.5);
      float d5 = cube(p,vec3(0.02,0.9,0.9));

      d = min(d,max(max(d5,-d6),-dk1));

    return vec4(mat,d/s);
}

vec4 map(vec3 p){
