    p -= vec3(-43.7,3.2,-9.5);
    p.yz *= rot(0.5);
    p.xy *= rot(0.2);
    p.xz *= rot(2.3);
    float sx = 7.9;
    float sc = 2.8*pow((1.0-(p.x+sx)/(2.0*sx)),2.0);
    p.y += sin(time+p.x);
    float d1 = cube(p,vec3(sx,1.*sc,1.*sc))-2.0*fbm(p.xy*1.2-time*vec2(-2.,1.));
  return vec4(-0.6*vec3(1.0),d1);
}

vec4 volumeFm(vec3 p){
    float time = iTime;
    p -= vec3(-43.7,3.2,-9.5);
    p.yz *= rot(0.5);
    p.xy *= rot(0.2);
    p.xz *= rot(2.3);
    float sx = 4.9;
    float sc = 1.4*(1.0-(p.x+sx)/(2.0*sx));
    p.y += sin(time+p.x);
    float d1 = cube(p,vec3(sx,1.*sc,1.*sc))-2.0*fbm(p.xy*1.1-time*vec2(-2.,1.));
  return vec4(clamp((1.0-sc*0.9),0.0,1.0)*vec3(1.0,0.2,0.0),d1);
