float softray(vec3 ro, vec3 rd , float hn)
{
	float t = 0.01;
	float jt = 0.0;
	float res = 1.;
	for (int i = 0; i < 20; ++i) {
		jt = map(ro+rd*t).w;
		res = min(res,jt*hn/t);
		t = t+ clamp(0.02,10.,jt);
	}
	return clamp(res,0.0,1.0);
}

float ao(vec3 p,vec3 n,float len,float power){
	float oss =0.0;
	for(int i =0;i<3;i++){
		float d = map(p+n*len/3.0*float(i+1)).w;
		oss += (len-d)*power;
		power *=0.5;
	}
	return clamp(1.-oss,0.0,1.0);
}


