vec3 lp1 = normalize(vec3(0.7, 0.6, -0.7));

vec3 ACESFilm(vec3 x)
{
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

float smin( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float smax( float d1, float d2, float k )
{
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h);
}

float sdBox(vec3 p, vec3 b)
{
    return length(max(abs(p) - b, 0.0));
}

float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

float sdPlane(vec3 p)
{
    return p.y;
}

vec2 sdWorld(vec3 p) {
    float dp = length(p);
    float d = 0.0;

    float s1, b1, p1;

    // sphere
    {
        vec3 pp = p;
        float bf = pow(abs(sin(iTime * 4.0)), 0.8);
        pp -= vec3(
            cos(iTime * 4.0) * 2.5,
            bf*5.0,
            -cos(iTime * 2.0 + PI/2.0) * 3.);
        s1 = sdSphere(pp, 1.0);
    }

    // box
    {
        vec3 pp = p + vec3(
            cos(p.z*0.4 + iTime*4.0)*4.0,
            -1.0 + sin(p.z-iTime*4.0)*0.5,
            0.0);
        pp.y += pow(abs(p.z*0.03), 11.0);
        float r = max(0.0, 0.25);
        vec3 br = vec3(r, r, 100.0);
        b1 = sdBox(pp, br);
        b1 -= 0.3; // rounding
        b1 *= 0.5; // fix marcher

        d = min(s1, b1);
    }

    // ground
    {
        vec3 pp = p;
        float dirt = textureLod(iChannel0, p.xz*0.02, 0.0).x;
        float cd = dp;
		cd *= 0.5 + 0.25 * (cos(p.x/30.0) + sin(p.z/20.0));
		pp.y -= pow(cd*0.015, 2.5);
        dirt *= (1.0 - min(1.0,cd*0.005));
        dirt *= 1.5;
        pp.y += dirt;

        p1 = sdPlane(pp+vec3(0.0, 0.0, 0.0));

        // slight performance enhancement
        if(dp < 100.0)
        {
            // mirrored hills with holes in em
        	float hillo = textureLod(iChannel0, p.xz*0.005, 0.0).y;
            float hilld = 85.0;
            pp.z = -abs(pp.z); // mirror
            pp.y -= hillo * 5.0;
            float hill1 = sdSphere(pp+vec3(0,30.,hilld), 50.0);
            p1 = smin(p1, hill1, 20.0);
            pp.x *= 2.4;
            pp.y *= 1.1;
            pp.z *= 1.0;
            pp -= -(1.0+2.0*vec3(hillo,0,hillo))*2.0;
            float hillh1 = sdSphere(pp+vec3(0,12.,hilld-48.), 21.0);
            p1 = smax(hillh1, p1, 3.0);
        }

        d = min(d, p1);
    }

    // floor is 1, white is 2
    float mat = 100.0;
    if(p1<=d) mat = 1.0;
    if(b1<=d) mat = 2.0;
    if(s1<=d) mat = 3.0;

    return vec2(d, mat);
}

vec4 trace(vec3 ro, vec3 rd)
{
    const int ITERS = 200;

    vec3 p = ro;
    vec2 d;

    for(int i = 0; i < ITERS; i++)
    {
        d = sdWorld(p);

        if(d.x <= EPSILON) {
            return vec4(p, d.y);
        }
        if(d.x >= INFINITY) {
            return vec4(p, 0.0);
        }

        p += rd * d.x;
    }

    return vec4(p, d.y);
}

vec3 normal(vec3 p)
{
    vec2 e = vec2(EPSILON, 0.0);
    return normalize(vec3(
    	sdWorld(p+e.xyy).x - sdWorld(p-e.xyy).x,
        sdWorld(p+e.yxy).x - sdWorld(p-e.yxy).x,
        sdWorld(p+e.yyx).x - sdWorld(p-e.yyx).x
	));
}

float softShadow(vec3 ro, vec3 rd)
{
    const int ITERS = 30;

    float nearest = 1.0;

    float t = 0.1;
    for(int i = 0; i < ITERS; i++)
    {
        vec3 p = ro + rd * t;
        float d = sdWorld(p).x;

        float od = d / t;

        if(od < nearest) {
            nearest = od;
        }
        if(d <= EPSILON) {
			return 0.0;
        }
        if(d >= INFINITY) {
            break;
        }

        t += min(0.5, max(EPSILON, d));
    }

    return nearest;
}

vec3 shadeSky(vec3 rd)
{
    vec3 up = vec3(0.0, 1.0, 0.0);
    float du = max(0.0, dot(rd, up));
    float id = 1.0 - du;
    id = pow(id, 2.0);

    float dl = max(0.0, dot(rd, lp1));
    dl = pow(dl, 5.0);
    dl += pow(dl, 4.0);
    vec3 lc = vec3(1.0, 0.9, 0.9);

    float hz = pow(max(0.0, 1.0 - du*0.6), 30.0);
    vec3 hzc = vec3(1.0, 0.85, 0.85);

    vec3 bc = vec3(0.4, 0.6, 0.9);

    return mix(bc*id + lc*dl, hzc, hz);
}

vec3 shadeWorld(vec4 pm, vec3 ro, vec3 n)
{
    vec3 lp2 = vec3(0.0, -1.0, 0.0);
    vec3 lp3 = -lp1;

    float mat = pm.w;
    vec3 p = pm.xyz;
    float d = length(p);
    vec3 r = reflect(normalize(p-ro), n);

    vec3 amb = vec3(0.15, 0.16, 0.2);
    vec3 alb = vec3(1.0);
    vec3 salb = vec3(1.0);
    vec3 l3alb = vec3(1.0,0.7,0.5);

    if(mat < 1.5) {
        // floor
        amb *= 0.7;
        alb = vec3(1.0,0.7,0.5);
        salb = vec3(0);
        l3alb = vec3(0);
    } else if (mat < 2.5) {
        // band
        amb = vec3(0.3,0.2,0.25);
        alb = vec3(1.0,0.4,0.4);
    }

    float l1 = max(0.0, dot(n, lp1));
    float l2 = max(0.0, dot(n, lp2));
    float l3 = max(0.0, dot(n, lp3));

    float spec1 = pow(max(0.0, dot(r, lp1)), 50.0);

    float s1 = 1.0;
    if(d <= SHADOW_AREA)
    {
		s1 = softShadow(p, lp1);
    	s1 = max(0.2, min(1.0, s1 * 4.0));
    }

    return  amb * alb +
        	alb * l1 * s1 +
        	alb * l3 * l3alb * 0.15 +
        	salb * spec1;
}

vec3 shade(vec4 hit, vec3 ro, vec3 rd)
{
    vec3 col;
    vec3 p = hit.xyz;
    float d = length(p - ro);

    if(hit.w < 0.5) {
        col = shadeSky(rd);
    } else {
    	vec3 n = normal(p);
        vec3 sky = shadeSky(rd);
        vec3 world = shadeWorld(hit, ro, n);
        float dm = min(pow(d*0.003, 1.8), 1.0);
        col = mix(world, sky, dm);

        if(DO_REFLECTION && hit.w>2.5 && hit.w<3.5)
        {
            // ball
            vec3 r = reflect(rd, n);
            vec4 rhit = trace(p+r*EPSILON, r);
            vec3 rn = normal(rhit.xyz);
            vec3 rc;
            if(rhit.w<0.5) rc = shadeSky(r);
            else rc = shadeWorld(rhit, p, rn);
            col = mix(col, rc, 0.3);
        }
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv - vec2(0.5)) * 2.0;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime*0.4 + PI*0.3 + iMouse.x/iResolution.x*PI*2.0;

    vec3 ro = vec3(0.0, 4.0, -15.0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.8));

    mat3 rot = mat3(
        cos(t), 0,	sin(t),
        0,		1,	0,
        -sin(t),0,	cos(t));
    ro *= rot;
    rd *= rot;

    vec4 hit = trace(ro, rd);

    vec3 col = shade(hit, ro, rd);

	col = ACESFilm(col);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float rectangle_1(vec2 center, float width, float height, vec2 uv, float blur, vec2 offset){
	center += offset;
    float wLen = abs(uv.x - center.x);
    float hLen = abs(uv.y - center.y);

    float wCol = smoothstep(width, width - blur, wLen);
    float hCol = smoothstep(height, height - blur, hLen);

    return wCol * hCol;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 backGroundColor = vec3(1.0,1.0,0.5);

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv *= 2.0;
    uv.x *= iResolution.x / iResolution.y;

    vec2 center = vec2(0.0);
    float width = 0.8;
    float height = 0.8;
    float blur = 0.1;
    vec2 offset = vec2(.0);

    float col = rectangle_1(center, width, height, uv, blur, offset);

    fragColor = vec4(backGroundColor * vec3(abs(col)), 1.0);
}

