#define FOV 1.5
#define R 140.

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

float particleDistance(int id, vec2 p)
{
    return distance(getParticle(id).xy, p);
}

float gauss(float x, float r)
{
    x/=r;
    return exp(-x*x);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

#define freq 15.
float DE(vec3 pos)
{
    pos.xy += size*0.5;
    float de = 1e10;
    ivec2 p = ivec2(pos.xy);
    get(p);
   	float y = 0.;

    for(int i = 0; i < CN; i++)
    {
        vec4 p0 = getParticle(ids[i]); 
        float d = distance(pos.xy, p0.xy);

        float ph = 6.*hash11(float(ids[i]));
        float osc = (0.5+0.5*sin(freq*iTime+ph));
        y += -2.*sin(-freq*iTime-ph)*sin(d)/(k*d+1.);

        de = min(de, sdSphere(pos - vec3(p0.xy, 4.*prad*osc+4.5), 0.5*prad));
    }
    de = min(de, 0.8*sdBox(pos - vec3(size, 4.*y)*0.5, vec3(size*0.5, 3.)));
    return de;
}


vec4 calcNormal(vec3 p, float dx) {
	const vec3 K = vec3(1,-1,0);
	return  (K.xyyx*DE(p + K.xyy*dx) +
			 K.yyxx*DE(p + K.yyx*dx) +
			 K.yxyx*DE(p + K.yxy*dx) +
			 K.xxxx*DE(p + K.xxx*dx))/vec4(4.*dx,4.*dx,4.*dx,4.);
}

#define M 128.
#define min_d 0.5
vec4 ray_march(vec3 p, vec3 r)
{
    float d;
    for(float i = 0.; i < M; i++)
    {
        d = DE(p); 
        p += r*d;
        if(d < min_d || d > size.x) break;
    }
    return vec4(p, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates 
    vec2 pos = (fragCoord - iResolution.xy*0.5)/max(iResolution.x,iResolution.y);

    vec2 uv = iMouse.xy/iResolution.xy;
    vec2 angles = vec2(uv.x*2., 0.3*uv.y - 0.3)*PI;

    vec3 camera_z = vec3(cos(angles.x)*cos(angles.y),sin(angles.x)*cos(angles.y),sin(angles.y));
    vec3 camera_x = normalize(vec3(cos(angles.x+PI*0.5), sin(angles.x+PI*0.5),0.)); 
    vec3 camera_y = -normalize(cross(camera_x,camera_z));

    //tracking particle
    vec4 fp = getParticle(0);

    vec3 ray = normalize(camera_z + FOV*(pos.x*camera_x + pos.y*camera_y));
    vec3 cam_pos = vec3(fp.xy-size.xy*0.5, 0.) - R*vec3(cos(angles.x)*cos(angles.y),sin(angles.x)*cos(angles.y),sin(angles.y));

    vec4 sphere = vec4(0.,0.,0., 1.);

    vec4 X = ray_march(cam_pos, ray);

    if(X.w < min_d)
    {
    	vec4 N0 = calcNormal(X.xyz, 0.6);
   		vec3 n = normalize(N0.xyz);
        vec3 rd = reflect(ray, n);
        vec3 rfd = refract(ray, n, 1.15);
        vec3 col = texture(iChannel2,  rd.yzx).xyz;
        vec3 col1 = texture(iChannel2,  rfd.yzx).xyz;
        float K = 1. - pow(max(dot(n,rd),0.), 3.);
        fragColor.xyz = col*K + 0.6*col1*(1.-K);
    }
    else
    {    
        //background
        fragColor = texture(iChannel2,  ray.yzx);
    }

}

