//force scalar
float F(float d)
{
    d /= 2.*prad;
    return -ff*sin(d)/(k*d);
}

//force vector
vec2 Fv(vec2 p0, vec2 p1)
{
    if(!all(equal(p0,p1)))
   	 return normalize(p1-p0)*F(distance(p1,p0));
    else
     return vec2(0.);
}

//get particle from array
vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);
    if(p.x < N.x && p.y < N.y)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch1, pos);

        int id = xy2i(p);
        //neighbors
   		get(ivec2(U.xy));

        vec2 F = vec2(0.,0.);

        vec4 p0 = getParticle(ids[0]);
        for(int i = 1; i < CN; i++)
        {
            vec4 pi = getParticle(ids[i]);
        	F += Fv(U.xy, pi.xy);    
        }

        if(isKeyPressed(KEY_SPACE)) F *= 3.;

        if(id == 0)
        {
            //keyboard controls lol
            vec2 uv = iMouse.xy/iResolution.xy;
            vec2 angles = vec2(uv.x*2., 0.3*uv.y - 0.3)*PI;

            vec3 camera_z = vec3(cos(angles.x)*cos(angles.y),sin(angles.x)*cos(angles.y),sin(angles.y));
            vec3 camera_x = normalize(vec3(cos(angles.x+PI*0.5), sin(angles.x+PI*0.5),0.)); 
            vec3 camera_y = -normalize(cross(camera_x,camera_z));
            vec2 dF = vec2(0.);

       		if(isKeyPressed(KEY_A)) dF -= normalize(camera_x.xy);
            if(isKeyPressed(KEY_D)) dF += normalize(camera_x.xy);
            if(isKeyPressed(KEY_W)) dF += normalize(camera_y.xy);
            if(isKeyPressed(KEY_S)) dF -= normalize(camera_y.xy);
            F += 15.*dF;
        }

        U.zw += dt*F;
        U.zw *= 1. - 0.004*dt*length(U.zw); //cooling
        U.xy += U.zw*dt;

        //border conditions
        if(size.x - U.x < 2.) U.z = -abs(U.z);
        if(U.x < 2.) U.z = abs(U.z);
        if(size.y - U.y < 2.) U.w = -abs(U.w);
        if(U.y < 2.) U.w = abs(U.w);

        //this pixel value
        if(iFrame<3)
        {
            U.xy = size*hash22(pos+395.)+1.;
			U.zw = 2.*(hash22(3.14159*pos) - 0.5);
        }
    }
    else discard;
}

