void insert(int id, float d, int i)
{
    //shift all by 1
    for(int j = CN-1; j > i; j--)
    {
        ids[j] = ids[j-1];
        ds[j] = ds[j-1];
    }

    //insert
    ids[i] = id; 
    ds[i] = d;
}

void sort(int utemp, ivec2 p)
{
    vec2 pos = vec2(p)+1.;
    float dtemp = particleDistance(utemp, pos);  
    //sorting
    for(int i = 0; i < CN; i++)
    {
        if(ids[i] == utemp) return;

        if(ids[i] < 0 || ds[i] >= dtemp)
        {
            insert(utemp, dtemp, i);
            return;
        }
    }
}

void upd(ivec2 p, ivec2 dx)
{
    int t[CN];
    getA(t, max(p + dx, ivec2(0,0)));

	for(int i = 0; i < CN; i++)
    {
   		if(t[i]>=0)
        	sort(t[i], p);
    }
}

void randupd(ivec2 p )
{
    for(int i = 0; i < 6; i++)
    {
        //go through the entire array sudorandomly
        sort(((iFrame + p.x + p.y*int(iResolution.x))^(1<<i))%tot_n, p);
    }
}

void updrad(ivec2 p, int R)
{
    int r = 1<<R;
    upd(p, ivec2(r, 0));
    upd(p, ivec2(-r, 0));
    upd(p, ivec2(0, r));
    upd(p, ivec2(0, -r));
}

void update(ivec2 p)
{
    randupd(p);
    upd(p, ivec2(0));
    updrad(p, 0);
    updrad(p, 2);
}

void mainImage( out vec4 U, in vec2 pos )
{
    pos *= 2.;
    if(pos.x < iResolution.x && pos.y < iResolution.y) //8x voronoi particle tracking but in 2x lower res
    {
        ivec2 p = 2*ivec2(pos/2.);

        init();

        update(p);

        U = save();
    } else discard;
}

