    {
        #if MODE == 0
        col = Tonemap_ACESFitted2(col);
        #elif MODE == 1
        col = Tonemap_ACESFitted(col);
        #else
        col = RRTAndODTFit(col);
        #endif
    }

    fragColor = vec4(sRGB_InvEOTF(clamp(col, 0.0, 1.0)), 0.0);
