// Lincense: CC0 (https://creativecommons.org/publicdomain/zero/1.0/)

/*
Good intro to ACES: https://chrisbrejon.com/cg-cinematography/chapter-1-5-academy-color-encoding-system-aces/

On Average the rendering with sharp RGB primaries produces results closer to the spectral refrence:
https://computergraphics.stackexchange.com/questions/8152/for-shader-math-why-should-linear-rgb-keep-the-gamut-of-srgb

Rendering in ACEScg retains bounce colors better than rendering in sRGB. 
That is because the AP1 primaries (i.e. ACEScg RGB primaries) cover a wider gamut than the Rec. 709 primaries which sRGB uses.
This means that saturated sRGB colors are not fully saturated in ACEScg.
You can still get strongly saturated colors by picking colors in ACEScg directly instead of doing so in sRGB.

However, picking colors in sRGB and converting them to ACEScg before rendering has two advantages.

First, the sRGB gamut lies largely inside and covers a decent portion of the Pointer's gamut, a comprehensive sampling of naturally occuring albedos.
So using sRGB to pick albedos ensures your values to be reasonable.
(see Rec. 709 / sRGB section here: https://www.tftcentral.co.uk/articles/pointers_gamut.htm)

Second, since ACEScg has a wider gamut than sRGB, there is no guarantee that your rendering results will be in gamut (for LDR displays).
Picking colors in sRGB mitigates that problem even if it doesn't completely solve it.
For example bounce colors can still get saturated to a point where they are not longer in sRGB gamut. 
So softclipping the rendering+tonemapping result before applying the sRGB EOTF might be a good idea.

camera controls via mouse + shift key
light controls via WASD/Arrow keys

Common:  settings
BufferA: rendering
Image:   tonemapping

*/

#define KEY_LEFT  37
#define KEY_UP    38
#define KEY_RIGHT 39
#define KEY_DOWN  40

#define KEY_SHIFT 0x10
#define KEY_A 0x41
#define KEY_D 0x44
#define KEY_S 0x53
#define KEY_W 0x57

#define KeyBoard iChannel1

float ReadKey(int keyCode) {return texelFetch(KeyBoard, ivec2(keyCode, 0), 0).x;}


#define VarTex iChannel0
#define OutCol outCol
#define OutChannel w

#define WriteVar(v, cx, cy) {if(uv.x == float(cx) && uv.y == float(cy)) OutCol.OutChannel = v;}
#define WriteVar4(v, cx, cy) {WriteVar(v.x, cx, cy) WriteVar(v.y, cx, cy + 1) WriteVar(v.z, cx, cy + 2) WriteVar(v.w, cx, cy + 3)}

float ReadVar(int cx, int cy) {return texelFetch(VarTex, ivec2(cx, cy), 0).OutChannel;}
vec4 ReadVar4(int cx, int cy) {return vec4(ReadVar(cx, cy), ReadVar(cx, cy + 1), ReadVar(cx, cy + 2), ReadVar(cx, cy + 3));}


// sRGB => XYZ => D65_2_D60 => AP1
const mat3 sRGBtoAP1 = mat3
(
	0.613097, 0.339523, 0.047379,
	0.070194, 0.916354, 0.013452,
	0.020616, 0.109570, 0.869815
);

vec3 MapColor(vec3 srgb)
{
    #if MODE == 0
    return srgb * sRGBtoAP1;
    #else
    return srgb;
    #endif
}

float Intersect_Scene(
vec3 rp, vec3 rd,
out float t, out vec3 n, out vec3 a)
{
     float hit;

    {
    	vec2 tt; vec3 n0, n1;
		float th = Intersect_Ray_Cube(rp, rd, vec3(0.0, -0.8, 0.0), vec3(1.5, 0.05, 1.5), /*out:*/ tt, n0, n1);

        hit = th > 0.0 ? 1.0 : 0.0;
        t = tt.x;
        n = n0;
        a = MapColor(AlbedoPlane0);

        vec3 p = rp + rd * tt.x;
        if((p.x > 0.0) == (p.z > 0.0)) a = MapColor(AlbedoPlane1);
    }

    {
        vec2 tt;
		float th = Intersect_Ray_Sphere(rp, rd, vec3(0.0), 0.5, /*out:*/ tt);

        if(th > 0.0)
        if(hit <= 0.0 || tt.x < t)
        {
            t = tt.x;
            n = normalize(rp + rd * tt.x);
            a = MapColor(AlbedoSphere);

            hit = 1.0;
        }
    }

    return hit;
}


void mainImage( out vec4 outCol, in vec2 uv0 )
{     
    vec2 uv = uv0.xy - 0.5;
	vec2 tex = uv0.xy / iResolution.xy;
    vec2 tex21 = tex * 2.0 - vec2(1.0);

    vec4 mouseAccu  = ReadVar4(1, 0);
    vec4 wasdAccu   = ReadVar4(2, 0);
    float frameAccu = ReadVar (3, 0);

    vec2 ang = vec2(Pi * 0.3, -Pi * 0.02);
    ang += mouseAccu.xy * 0.008;

    Cam cam = NewCam(ang, Pi * 0.5, iResolution.x / iResolution.y);
    //vec3 cpos = -cam.Front * 2.5;
    float cdist = exp2(1.0 + mouseAccu.w * 0.02);
    vec3 cpos = -cam.Front * cdist;

    vec3 pxId  = vec3(frameAccu, uv); 
         pxId *= vec3( 0.76032, 1.47035, 0.92526); 
         pxId += vec3(-0.69060, 0.02293, 0.68109);

    uint h = WellonsHash(uvec3(uv, iFrame), 0u).x;

    {
        vec2 off;
        {
            // filter kernel:
            float h0 = Hash11(h);
            float h1 = Hash11(h);

        	off = vec2(Sample_Triangle(h0), 
                       Sample_Triangle(h1));
        }

        #if 1
        {
            // bloom kernel:
            float h0 = Hash11(h);
            float h1 = Hash01(h);

        	off += AngToVec(h0 * Pi) * Pow2(log2(1.0 - Pow2(h1))) * 0.25;
        }
        #endif

        tex21 = (uv0.xy + off) / iResolution.xy * 2.0 - vec2(1.0);
    }

    vec2 llp;// local sample pos on lens
    {
        float h0 = Hash11(h);
        float h1 = Hash01(h);

        llp = Sample_Disk(h0, h1);
        llp *= 0.05;
    }

    vec3 glp;// global sample pos on lens
    float S1 = cdist - 0.0;// set sphere sharp
    vec3 rdir = NewRay(cam, tex21, llp, S1, /*out:*/ glp);

    vec2 lightAng = vec2(Pi * 1.0, Pi * 0.3);
    lightAng.x += (wasdAccu.y - wasdAccu.w) * 0.06; 
    lightAng.y += (wasdAccu.x - wasdAccu.z) * 0.04;    

    vec3 light0 = AngToVec(lightAng);
    vec3 light = light0;
    {
        float h0 = Hash11(h);
        float h1 = Hash01(h);

        h1 = mix(0.999, 1.0, h1);

        light = Sample_Sphere(h0, h1, light);
    }


    vec3 pot = vec3(1.0);
    vec3 col = vec3(0.0);

    vec3 pos = cpos + glp;
    vec3 dir = rdir;

    for(float i = 0.0; i < 4.0; ++i)
    {
        bool hit; float t; vec3 n; vec3 a;
        hit = Intersect_Scene(pos, dir, /*out:*/ t, n, a) == 1.0;

        if(hit)
    	{
    	    vec3 p = pos + dir * t;

    	    pot *= a;

    	    float sh;
    	    {
    	        float t2; vec3 n2; vec3 a2;
    	        sh = Intersect_Scene(p + n * 0.0001, light, /*out:*/ t2, n2, a2);
    	        sh = 1.0 - sh;
    	    }

    	    col += pot * clamp01(dot(light, n)) * sh * 1.0;// * RcpPi;// impl scale prim light E by Pi

    	    pos = p + n * 0.0001;
    	    {
    	        float h0 = Hash11(h);
    	    	float h1 = Hash01(h);

    	    	dir = Sample_ClampedCosineLobe(h0, h1, n);
    	    }

    	} 
    	else 
    	{
    	    //col += pot * MapColor(textureLod(iChannel2, dir, 0.0).rgb);

    	    if(i == 0.0 && dot(light0, dir) > 0.999) col += vec3(Pi);

    	    break;
    	}
    }


    vec3 colLast = textureLod(iChannel0, tex, 0.0).rgb;

    col = mix(colLast, col, 1.0 / (frameAccu + 1.0));    

    outCol = vec4(col, 0.0);


    {
        vec4 iMouseLast     = ReadVar4(0, 0);
        vec4 iMouseAccuLast = ReadVar4(1, 0);
        vec4 wasdAccuLast   = ReadVar4(2, 0);
        float frameAccuLast = ReadVar (3, 0);
        float modeLast      = ReadVar (4, 0);


        bool shift = ReadKey(KEY_SHIFT) != 0.0;

        float kW = ReadKey(KEY_W);
        float kA = ReadKey(KEY_A);
        float kS = ReadKey(KEY_S);
        float kD = ReadKey(KEY_D);

        float left  = ReadKey(KEY_LEFT);
        float right = ReadKey(KEY_RIGHT);
        float up    = ReadKey(KEY_UP);
        float down  = ReadKey(KEY_DOWN);


        bool anyK = false;

        anyK = anyK || iMouse.z > 0.0;
        anyK = anyK || shift;
        anyK = anyK || kW != 0.0;
        anyK = anyK || kA != 0.0;
        anyK = anyK || kS != 0.0;
        anyK = anyK || kD != 0.0;
        anyK = anyK || left  != 0.0;
        anyK = anyK || right != 0.0;
        anyK = anyK || up    != 0.0;
        anyK = anyK || down  != 0.0;


        frameAccuLast += 1.0;
        if(anyK || float(MODE) != modeLast) frameAccuLast = 0.0;


        vec4 wasdAccu = wasdAccuLast;
        wasdAccu += vec4(kW, kA, kS, kD);
        wasdAccu += vec4(up, left, down, right);        


        vec2 mouseDelta = iMouse.xy - iMouseLast.xy;

        bool cond0 = iMouse.z > 0.0 && iMouseLast.z > 0.0;
        vec2 mouseDelta2 = cond0 && !shift ? mouseDelta.xy : vec2(0.0);
        vec2 mouseDelta3 = cond0 &&  shift ? mouseDelta.xy : vec2(0.0);

        vec4 iMouseAccu = iMouseAccuLast + vec4(mouseDelta2, mouseDelta3);


        WriteVar4(iMouse,        0, 0);
        WriteVar4(iMouseAccu,    1, 0);
        WriteVar4(wasdAccu,      2, 0);
        WriteVar (frameAccuLast, 3, 0);
        WriteVar (float(MODE)  , 4, 0);
    }
}

