/* Code by Sixclones

My boilerplate: https://www.shadertoy.com/view/wdsyzH */

// CONSTANTS & UTILS
#define QP 0.785398163397448
#define HP 1.570796326794897
#define P 3.141592653589793
#define TP 6.283185307179586

#define t iTime
#define ht 0.5 * t
#define tt 0.1 * t

#define S(a, b, t) smoothstep(a, b, t)

// UTILS FN
/* map a value `n` from a range `start1` -> `stop1` to a range `start2` -> `stop2` */
float map(float n, float start1, float stop1, float start2, float stop2) {
  return (n - start1) / (stop1 - start1) * (stop2 - start2) + start2;
}

/* map a value `n` from the range -1.0 -> 1.0 to a range `start2` -> `stop2`
to be used with cos/sin
`map01(sin(x), 0.0, 1.0)` */
float map01(float n, float start2, float stop2) {
  return (0.5 * n + 0.5) * (stop2 - start2) + start2;
}

vec2 map01(vec2 n, float start2, float stop2) {
  return (0.5 * n + 0.5) * (stop2 - start2) + start2;
}

/* rotate uv
uv - uv to rotate
a - angle of the rotation

inspired by Patricio Gonzalez Vivo (but in fact you find this everywhere)
https://github.com/patriciogonzalezvivo/PixelSpiritDeck/blob/master/lib/rotate.glsl
*/
vec2 rotate2d(vec2 uv, float a) {
  uv = mat2(cos(a), -sin(a), sin(a), cos(a)) * uv;
  return uv;
}

/* function to draw filled sdf
x - sdf to draw
s - size

from Pixel Spirit Deck by Patricio Gonzalez Vivo
https://github.com/patriciogonzalezvivo/PixelSpiritDeck/blob/master/lib/fill.glsl */
float fill(float x, float s) {
  return 1.0 - step(s, x);
}

/* function to draw stroke
x - sdf to draw
s - size of the draw
w - width of the stroke
p - precision of the stroke

inspired by Pixel Spirit Deck `stroke` function by Patricio Gonzalez Vivo
https://github.com/patriciogonzalezvivo/PixelSpiritDeck/blob/master/lib/stroke.glsl */
float stroke(float x, float s, float w, float p) {
  return clamp(
    S(x - 0.5 * w - p, x - 0.5 * w + p, s) * S(x + 0.5 * w + p, x + 0.5 * w - p, s),
    0.0, 1.0
  );
}

/* compute a rect signed distance field
uv - uv of the screen
s - size of the rect

from Pixel Spirit Deck by Patricio Gonzalez Vivo
https://github.com/patriciogonzalezvivo/PixelSpiritDeck/blob/master/lib/rectSDF.glsl */
float rectSDF(vec2 uv, vec2 s) {
  return max(abs(uv.x / s.s), abs(uv.y / s.y));
}

// MAIN
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  	vec2 uv = fragCoord.xy / iResolution.xy;
  	vec2 uv0 = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

  	vec3 color = vec3(0.95);

  	// VIGNETTE
  	color -= S(0.55, 1.5, length(uv0));

  	// MASK
    // compute the sdf of a rect
  	float rect = rectSDF(rotate2d(uv0, QP), vec2(0.5));

    // get a filled rect
  	float rectf = fill(rect, 0.65);
    // color -= rectf; // uncomment to see the result of a fill


  	// WAVES + MOIR = MAGIC
    /* split between left & right, a more simple writing is `mix(-ht, ht, step(0.0, uv0.y))`
	the step is done on 0.0 because uv0 goes from -1.0 to 1.0
	finally the split is rotated
    */
  	float timer = mix(
    	-ht, ht,
    	step(0.0, rotate2d(uv0, -ht + cos(ht + QP * uv.y)).x)
  	);

    /* amplitude of the waves */
  	float amp = map01(cos(t + TP * uv.x), 0.1, 0.75);

    /* waves are computed with a sinus combined with lot of offsets and cos/sin */
  	float waves = amp * sin(t + 5.0 * TP * uv.x + 2.5 * P * uv.y
    	+ P * cos(-t + 5.0 * TP * uv.x * pow(uv.y, uv.x))
		+ pow(cos(timer + P * uv.y), uv.x)
  	);

    // subtle offset
  	float offset = t + uv.x;

  	// STRIPES
    /* number of stripes */
  	float ns = 30.0;

    /* passing this to a cos/sin will give you stripes
	this could have been done with `fract(ns * uv.y)` */
  	float _stripes = ns * TP * uv.y;

    /* compute static + animated stripes and */
  	vec2 stripes = map01(vec2(
    	cos(_stripes),
    	cos(_stripes + waves + offset)
  	), 0.0, 1.0);

  	// DRAWING PARAMS
    /* normally size of the draw but with stripes if more like the position
	with 0.5 I assure that the stripe is in the middle of the render
	(not clear comment, I admit, sorry)
	*/
    float s = 0.5;
  	float w = 0.2; // width of strokes
  	float p = 50.0 / iResolution.x; // precision of strokes to avoid aliasing

    // RENDER
    // we create a mask by multiplying the addition of the stripes (static + animated) to the rect filled
    color -= rectf * (stroke(stripes.x, s, w, p) + stroke(stripes.y, s, w, p));

  	fragColor = vec4(color, 1.0);
}

/*

	Logarithmic Conical Spiral Center and Axis
	------------------------------------------

	When iteratively applying a transformtion matrix, the
	positions visited form a logarithmic spiral on a cone.

	This calculates the center (tip) and axis of that cone,
	as well as the angle between each iteration.

	The general approach is to ignore the scaling component
	of the matrix, such that multiple iterations form a
	cylinder instead of a cone. Once we have the axis and
	position of the cylinder, we can triangulate the tip of
	the cone.

	With these parameters, we can create a method that
	rotates and scales space over time to smoothly transform
	the second instance into the first, forming a
	seamless loop.

	See also the 2d version:
	https://www.shadertoy.com/view/tscBDH

*/

// Matrix functions
// --------------------------------------------------------

mat4 mTranslate(vec3 t) {
	return mat4(
        1, 0, 0, t.x,
        0, 1, 0, t.y,
        0, 0, 1, t.z,
        0, 0, 0, 1
   	);
}

mat4 mRotate(vec3 forward, vec3 up) {
    vec3 ww = normalize(forward * vec3(1,1,-1));
    vec3 uu = normalize(cross(up * vec3(-1,1,1),ww));
    vec3 vv = normalize(cross(ww,uu));
    return mat4(
        uu, 0,
        vv, 0,
        ww, 0,
        0, 0, 0, 1
    );
}

mat4 mScale(float s) {
	return mat4(
    	s, 0, 0, 0,
        0, s, 0, 0,
        0, 0, s, 0,
        0, 0, 0, 1
    );
}

float getScale(mat4 m) {
	return length(m[0].xyz);
}

vec3 mul(vec3 p, mat4 m) {
	return (vec4(p, 1) * m).xyz;
}


// Logarithmic conical spiral center, axis, and angle
// --------------------------------------------------------

#define PI 3.1415926

// Logarithmic spiral center
// https://www.shadertoy.com/view/tscBDH
vec2 spiralCenter(vec2 translation, float rotation, float scale) { 
    float l = sqrt(scale * scale - 2. * scale * cos(rotation) + 1.);
    float S = sin(rotation) * scale / l;
    float C = sqrt(1. - S * S);
    return mat2(-S, C, C, S) * translation.yx / l;
}

void conicalSpiral(mat4 txm, float txmScale, out vec3 axis, out float angle, out vec3 center) {

    // Remove scaling from transformation matrix
    mat4 txmns = mScale(1. / txmScale) * txm;

    // Get first four positions, these form a cylinder
    vec3 v0 = vec3(0);
    vec3 v1 = mul(v0, txmns);
    vec3 v2 = mul(v1, txmns);
    vec3 v3 = mul(v2, txmns);


	// Cylinder axis
    // -------------

    // Calculate normals for the two middle points
    vec3 n0 = v1 - mix(v0, v2, .5);
    vec3 n1 = v2 - mix(v1, v3, .5);

    // Cross for cylinder axis
    axis = normalize(cross(n0, n1));

    // Rotation matrix for cylinder direction
    mat3 mAxis = mat3(n0, cross(axis, n0), axis);


  	// Angle between iterations
    // ------------------------

    // Project points onto axis plane
    vec2 p1 = (v1 * mAxis).xy;
    vec2 p2 = (v2 * mAxis).xy;

    // Angle between the points
    angle = PI - acos(dot(normalize(-p1), normalize(p2 - p1)));    


	// Cone center (tip)
	// -----------------

    // Center of the 2d logarithmic spiral
    vec2 center2d = spiralCenter(p1, angle, txmScale);

    // Transform back into 3d
    center = vec3(center2d, 0) * inverse(mAxis);

    // Extrapolate the line between v0 and v1 to find the tip
    float v1Height = dot(v1, axis);
    float v1Radius = distance(center2d, p1);
    center += axis * v1Height * (length(center2d) / (length(center2d) - v1Radius));
}


// Camera movement
// --------------------------------------------------------

mat4 txm;
mat4 txmi;
float txmScale;

vec3 cameraAxis;
float cameraAngle;
vec3 cameraApex;

// Rotate around axis
// blackle https://suricrasia.online/demoscene/functions/
vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

// With the calculated spiral properties, rotate and scale space
// (aka move the camera), by a given amount until we have transformed
// the second iteration into the first iteration
float tweenCamera(inout vec3 p, float t) {
    float scale = pow(txmScale, t);
    p -= cameraApex;
    p = erot(p, cameraAxis, cameraAngle * t);
    p *= scale;    
    p += cameraApex;
    return scale;
}


// Modeling
// --------------------------------------------------------

float vmax(vec3 v) {
    return max(max(v.x, v.y), v.z);
}

float fBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, vec3(0))) + vmax(min(d, vec3(0)));
}

float fLine(vec3 p, vec3 n) {
    float t = dot(p, n) / dot(n, n);
    return length((n * t) - p) ;
}

struct Model {
	float d;
    vec3 p;
    int id;
};

Model opU(Model a, Model b) {
    if (a.d < b.d) {
    	return a;
    }
    return b;
}

Model mGizmo(vec3 p) {
	float d = fBox(p, vec3(.5, .5, .5));
   	return Model(d, p, 1);
}

Model mAxisAndCenter(vec3 p) {
    float axis = fLine(p - cameraApex, cameraAxis) - .08;
    float center = length(p - cameraApex) - .3;
    float d = min(axis, center);
   	return Model(d, p, 2);
}

Model scene(vec3 p) {

    Model model = mAxisAndCenter(p);

    float scale = 1.;
    vec3 pp = p;

    for (int i = 0; i < 15; i++) {
        // Draw gizmo
		Model gizmo = mGizmo(p);
        gizmo.d *= scale; // Fix distance for scale factor
        model = opU(model, gizmo);

        // Apply matrix and scale
        p = mul(p, txmi);
        scale *= txmScale;
    }

	scale = 1.;
    p = mul(pp, txm);

    for (int i = 0; i < 12; i++) {
        // Draw gizmo
		Model gizmo = mGizmo(p);
        gizmo.d *= scale; // Fix distance for scale factor
        model = opU(model, gizmo);

        // Apply matrix and scale
        p = mul(p, txm);
        scale /= txmScale;
    }

    return model;
}

Model map(vec3 p) {
    float scale = tweenCamera(p, mod(iTime * 2., 1.));
    Model model = scene(p);
    model.d /= scale;
    return model;
}



// Rendering
// --------------------------------------------------------

// Spectrum palette, iq https://www.shadertoy.com/view/ll2GD3
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}
vec3 spectrum(float n) {
    return pal( n, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    float t = iTime / 3. + .5;

    // Build the TRS transformation matrix for each iteration
    mat4 mT = mTranslate(vec3(0, cos(t/2.), -sin(t/2.)) * (cos(t/2.) * .25 + 1.5));
    mat4 mR = mRotate(vec3(cos(t) * -.5, cos(t/2.) * -.5, -1), vec3(sin(t) * -.5, 1, 0));
    mat4 mS = mScale(.8 + sin(t) * .05);
	txm = mS * mR * mT;
    txmScale = getScale(txm);
	txmi = inverse(txm);    

    // Find conical spiral parameters for camera movement
    conicalSpiral(txm, txmScale, cameraAxis, cameraAngle, cameraApex);


    // Ray marching

    float focalLength = 2.4;
    vec2 p = (-iResolution.xy + 2. * fragCoord.xy) / iResolution.y;

    vec3 rayDirection = normalize(vec3(p, -focalLength));
    vec3 rayOrigin = vec3(0,0,15);
    vec3 rayPosition = rayOrigin;
    float rayLength = 0.;

    Model model;
    float dist = 0.;
    vec3 bgcol = vec3(.014,.01,.02);
    vec3 color = bgcol;
    bool isBackground = false;

    for (float i = 0.; i < 100.; i++) {
        rayLength += dist;
        rayPosition = rayOrigin + rayDirection * rayLength;
        model = map(rayPosition);
		dist = model.d;

        if (dist < .001) {
            break;
        }

        if (rayLength > 50.) {
            isBackground = true;
            break;
        }
    }

    if ( ! isBackground) {
        if (model.id == 2) {
        	color = vec3(1);
        } else {
    		vec3 face = step(vec3(vmax(abs(model.p))), abs(model.p)) * sign(model.p);
            float faceIndex = max(vmax(face * vec3(0,1,2)), vmax(face * -vec3(3,4,5)));
    		color = spectrum(faceIndex / 6.);
    	}
    }

    float fog = 1. - exp((rayLength - 13.) * -.25);
    color = mix(color, bgcol, clamp(fog, 0., 1.));

    color = pow(color, vec3(1. / 2.2)); // Gamma
    fragColor = vec4(color, 1);
}

