/* Code by Sixclones

My boilerplate: https://www.shadertoy.com/view/wdsyzH */

// CONSTANTS & UTILS
#define QP 0.785398163397448
#define HP 1.570796326794897
#define P 3.141592653589793
#define TP 6.283185307179586

#define t iTime
#define ht 0.5 * t
#define tt 0.1 * t

#define S(a, b, t) smoothstep(a, b, t)

// UTILS FN
/* map a value `n` from a range `start1` -> `stop1` to a range `start2` -> `stop2` */
float map(float n, float start1, float stop1, float start2, float stop2) {
  return (n - start1) / (stop1 - start1) * (stop2 - start2) + start2;
}

/* map a value `n` from the range -1.0 -> 1.0 to a range `start2` -> `stop2`
to be used with cos/sin
`map01(sin(x), 0.0, 1.0)`
*/
float map01(float n, float start2, float stop2) {
  return (0.5 * n + 0.5) * (stop2 - start2) + start2;
}

/* function to draw stroke
x - sdf to draw
s - size of the draw
w - width of the stroke
p - precision of the stroke

inspired by Pixel Spirit Deck `stroke` function
https://github.com/patriciogonzalezvivo/PixelSpiritDeck/blob/master/lib/stroke.glsl
*/
float stroke(float x, float s, float w, float p) {
  return clamp(
    S(s + p, s - p, x - 0.5 * w) * S(s - p, s + p, x + 0.5 * w),
    0.0, 1.0);
}

/* function that render the waves
uv - uv of the screen
n - number of waves
id - identification of the cell
pre - precision of the waves
offset - offset used to change the shape/position of waves
*/
float waves(vec2 uv, float n, vec2 id, float pre, float offset) {
    // create a repetition of `n` lines on the y axis
    float x = fract(n * uv.y);

    /* define the width of each line by offsetting with `id.y`
	(by width I mean the wideness of the stroke)
	adding a subtle wavy effect inside the wave with `QP * uv.x`
	you exagerate it by multiplying by `10.0 * QP * uv.x`
	*/
  	float w = map01(
	    sin(-tt + id.y + QP * uv.x + cos(ht)),
    	0.1, 0.45);

    // make the wave moves and offset each line with `id.y` again
  	float s = map01(
	    cos(ht + HP * uv.x + id.y + offset),
    	1.0 - w, w);

    // combine everything
  	return stroke(x, s, w, pre);
}

// MAIN
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv0 = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

	// number of lines that oscillate between 6 and 10 with a nice curve
    float n = map01(
      sin(ht + sin(t - P * uv.y) + cos(-t + HP * uv.x)),
      6.0, 10.0);
  	vec2 id = floor(n * uv0);

    // start with a white background and substract the waves
  	vec3 color = vec3(1.0);

    // animate the precision
  	float pre = map01(cos(t + P * uv.y), 0.025, 0.125);
    // define a different offset for each color channel
  	vec3 offset = vec3(
    	0.5 * cos(t + 1.2 * P * uv.x),
    	0.25 * cos(t + P * uv.x),
    	0.1 * cos(t + 0.8 * P * uv.x)
  	);
    // make the magic happens
  	color.r -= waves(uv0, n, id, pre + 0.025, offset.x);
  	color.g -= waves(uv0, n, id, pre + 0.0, offset.y);
  	color.b -= waves(uv0, n, id, pre + 0.05, offset.z);

  	fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
    uv.y -= pow(abs(uv.x),1.3); uv.y *= 1.1;
    float t = iTime*3.;
    t = sin(t+sin(t+sin(t+sin(t))))*0.5 + 0.5;
    t = clamp(t,0.5,1.0);
    float mainheart = 0.5*t/length(uv);
    vec3 col = mainheart * vec3(0.2,0.1,0.05);
    fragColor = vec4(col,1.0);
}

