// Fork of "Bela's Ray Marcher" by bela333. https://shadertoy.com/view/3dlGDX
// 2020-03-13 21:43:36

//TODO: Refine
#define PI 3.14159265359
#define MAX_STEPS 255.0
#define MAX_DISTANCE 100.0
#define EPSYLON 0.0002
#define SKY_COLOR vec4(135., 206., 235., 255.)/255.



float sphereSDF(vec3 p, vec3 center, float radius){ 
	return length(p-center)-radius;
}

float planeSDF(vec3 p, float yPos){
	return abs(p.y-yPos)-EPSYLON*4.;
}

float cube2SDF(vec3 p, float size){
	return length(max(min(p, size),-size)-p);
}

float cubeSDF(vec3 p, vec3 center, float size){
	return cube2SDF(p-center, size);
}

//Modified version of iq's box SDF
float backdropSDF( vec2 p, float b )
{
  return -(length(max(p,0.0)) + min(max(p.x,p.y),0.0));
}

vec3 random_point_on_sphere(vec4 seed){
	float u = random(seed) * 2. * PI;
    float v = acos(2.*random(seed*2.) - 1.);

    return vec3(
    	cos(u)*sin(v),
    	sin(u)*sin(v),
        cos(v)
    );
}

vec3 random_point_on_hemisphere(vec3 n, vec4 seed){
	vec3 p = random_point_on_sphere(seed);
    return dot(n, p) > 0. ? p : -p;
}

void get_closer(float dist, int target_mat, inout float o, inout int material){
    if(dist < o){
        material = target_mat;
    }
    o = min(o, dist);
}

float getSDF(vec3 p, inout int material){
    const float drop_radius = 0.5;
    float backdrop = backdropSDF(-p.zy+vec2(-.5, drop_radius-.5), 1.0)+drop_radius;
    float cube = cubeSDF(p, vec3(0., 0., 0.), 0.5);
    float lamp = sphereSDF(p, vec3(1.0, 2., 1.), .5);
    lamp = min(lamp, sphereSDF(p, vec3(-1.0, 2., 1.), .5));
    float sphere = sphereSDF(p, vec3(0.0, 1.0, 0.0), .5);

    float o = backdrop;
    material = 3;
    get_closer(lamp, 2, o, material);
    get_closer(cube, 4, o, material);
    get_closer(sphere, 5, o, material);
	return o;
}

vec3 get_color(int material, vec3 normal, out vec3 emit, inout vec3 dir, vec4 seed, vec3 wi){
    switch(material){
        //Lamp
        case 2:
        	dir = vec3(0.0);
        	const float blueness = 1.125;
        	emit = vec3(.878, .941, blueness)*100./blueness;
        	return vec3(0.);
        //Backdrop
        case 3:
        	dir = random_point_on_hemisphere(normal, seed);
        	emit = vec3(0.);
        	return vec3(.769, .949, .306);
        //Box
        case 4:
        	dir = random_point_on_hemisphere(normal, seed);
        	emit = vec3(0.);
        	return vec3(1.0);
        case 5:
        	dir = -reflect(wi, normal);
        	emit = vec3(0.);
        	return vec3(1.0);
    }
}


vec3 approximateNormal(vec3 p){
	//float defaultDist = getSDF(p);
    vec2 sub = vec2(0, EPSYLON);
    int m = -1;
	vec3 normal = vec3(
    	getSDF(p+sub.yxx, m) - getSDF(p-sub.yxx, m),
        getSDF(p+sub.xyx, m) - getSDF(p-sub.xyx, m),
        getSDF(p+sub.xxy, m) - getSDF(p-sub.xxy, m)
    );
    return normalize(normal);
}

bool castRay(inout vec3 from, vec3 dir, inout int material){ 
	float allDistance = 0.;
    float i = 0.;
    float dist;
    for(i = 0.; i < MAX_STEPS; i++){ 
        vec3 p = from + dir*allDistance;
    	dist = getSDF(p, material);
        //float localAngle = atan(allDistance, dist);
        allDistance += dist;
        if(allDistance > MAX_DISTANCE || dist < EPSYLON) break;
    }
    from = from + dir*allDistance;
    if(dist > EPSYLON){
    	return false;
    }
    return true;
}

mat3 calcLookAtMatrix(vec3 origin, vec3 target, float roll) {
  vec3 rr = vec3(sin(roll), cos(roll), 0.0);
  vec3 ww = normalize(target - origin);
  vec3 uu = normalize(cross(ww, rr));
  vec3 vv = normalize(cross(uu, ww));

  return mat3(uu, vv, ww);
}



vec3 sample_(inout vec3 from, inout vec3 dir, vec4 seed, inout vec3 emit){
    vec3 wi = -dir;
    int material = -1;
	bool result = castRay(from, dir, material);
    vec3 normal = approximateNormal(from);

    if(!result){
    	emit=vec3(0.)*1.0;
        return vec3(0.0);
    }
    vec3 color = get_color(material, normal, emit, dir, seed, wi);
    from += dir * EPSYLON*5.;
    return color * dot(dir, normal);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 cameraPos = vec3(0.0, 0.0, 0.0);
    vec3 target = vec3(0.0, 0.0, 0.0);
    //iMouse.zw
    vec2 mouse = (iMouse.xy/iResolution.xy*2.)-.1;
    float updown = mouse.y*PI*.25;
    vec2 plane = vec2(cos(mouse.x*PI), sin(mouse.x*PI))*cos(updown);
    vec3 movement = vec3(plane.x, sin(updown), plane.y)*2.;
    cameraPos += movement;
    //cameraPos *= 2.5;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 seed = vec3(uv, iTime);
    vec2 aafc = fragCoord + vec2(random(vec4(seed, 0.0)), random(vec4(seed, 179.0)))-.5;
    //vec2 aafc = fragCoord;
    vec2 aauv = aafc/iResolution.xy;


    vec2 screenPos = aauv*2.0-1.0;


    vec2 convScreenPos = screenPos;
    convScreenPos.x *= iResolution.x/iResolution.y;

    vec3 gridPoint = vec3(convScreenPos, .875);
    gridPoint = normalize(gridPoint);
    gridPoint = calcLookAtMatrix(cameraPos, target, 0.0)*gridPoint;
    //gridPoint += cameraPos;
    vec3 color = vec3(1.0);
    vec3 emit = vec3(0.0);
    for(float i = 0.; i < 4.; i++){
        vec3 emit_color = vec3(0.0);
    	vec3 sample_color = sample_(cameraPos, gridPoint, vec4(seed, i), emit_color);
        emit += color * emit_color;
        color *= sample_color;
        if(length(gridPoint) == 0.){break;}
    }
    emit = max(emit, 0.0);
    vec4 previous = texture(iChannel0, uv);
    float previous_frame = -texelFetch(iChannel0, ivec2(0, 0), 0).x;

    if(iFrame==0){
    	previous_frame = 0.;
    }

    if(iMouse.w > 0.5){
    	previous_frame = float(iFrame);
    }

    if(fragCoord.x <= 2. && fragCoord.y <= 2.){
    	fragColor = vec4(-previous_frame, 0., 0., 1.);
        return;
    }else if(iMouse.w > 0.5){
    	fragColor = vec4(emit, 1.0);
        return;
    }

    fragColor = (previous*(float(iFrame)-previous_frame)+vec4(emit, 1.0))/(float(iFrame)-previous_frame+1.);
