    ro += vec3(0, t, -4.0*t) * 2.0;

    vec3 rd = normalize(vec3(uv, -1));
    rd.xz *= r2d(cos(t*PI*0.5) * 0.5);

    float d = mapTrace(ro, rd);
    vec3 p = ro + rd*d;
