vec4 curState = vec4(0);

/*
Equations of motion taken from:
https://www.myphysicslab.com/pendulum/double-pendulum-en.html
Returns:
x -> Angular velocity of pendulum 1 (dA1/dt)
y -> Angular velocity of pendulum 2 (dA2/dt)
z -> Angular acceleration of pendulum 1 (dV1/dt)
w -> Angular acceleration of pendulum 1 (dV2/dt)
*/
vec4 dAdt_dVdt(vec4 state)
{
    float a1 = state.x, //x = a1, Angle of pendulum 1
          a2 = state.y, //y = a2, Angle of pendulum 2
          v1 = state.z, //z = v1, Angular velocity of pendulum 1
          v2 = state.w; //w = v2, Angular velocity of pendulum 2

    vec2 dvdt = vec2(0);
    dvdt.x = -g*(2.0*m1 + m2)*sin(a1)-m2*g*sin(a1-2.0*a2)-2.0*sin(a1-a2)*m2*(v2*v2*l2 + v1*v1*l1*cos(a1-a2));
    dvdt.x /= l1*(2.0*m1+m2-m2*cos(2.0*a1-2.0*a2));

    dvdt.y = 2.0*sin(a1-a2)*(v1*v1*l1*(m1+m2)+g*(m1+m2)*cos(a1) + v2*v2*l2*m2*cos(a1-a2));
    dvdt.y /= l2*(2.0*m1+m2-m2*cos(2.0*a1-2.0*a2));

    return vec4(v1,v2,dvdt);
}

vec4 IntegrateRK4(vec4 state, float h)
{         
    vec4 k1 = h*dAdt_dVdt( state );
    vec4 k2 = h*dAdt_dVdt( state + k1/2.0 );
    vec4 k3 = h*dAdt_dVdt( state + k2/2.0 );
    vec4 k4 = h*dAdt_dVdt( state + k3 );

    state += (k1 + 2.0*k2 + 2.0*k3 + k4)/6.0;

    return state;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    curState = texelFetch(iChannel0,ivec2(fragCoord),0);
    bool reset = texelFetch(iChannel2,ivec2(0x20,0),0).x!=0.0; //Space = reset
    vec4 initState = SweepParams(uv);

    if(iFrame == 0 || reset)
    {
    	curState = initState;
    }

    for(int i=0;i<steps;i++)
    {
    	curState = IntegrateRK4(curState, dt / float(steps));
    }

    //Keep a1/a2 between -pi & +pi
    curState.x = mod(curState.x+tau/2.0,tau)-tau/2.0;
    curState.y = mod(curState.y+tau/2.0,tau)-tau/2.0;

    fragColor = vec4(curState);
}

