#define SWEEP_MODE A1_VS_A2

//Constants
const float tau = atan(1.0)*8.0;
const float pi = atan(1.0)*4.0;

//System Parameters
float g = 1.0; //Gravity
float m1 = 1.0, m2 = 1.0; //Mass on pendulum 1/2
float l1 = 0.3, l2 = 0.3; //Length of pendulum 1/2
float dt = 0.0167; //Time step per frame
const int steps = 10; //Steps per frame

//Visualization Parameters
