vec4 checkCol1 = vec4(0.17,0.17,0.17,1);
vec4 checkCol2 = vec4(0.20,0.20,0.20,1);
float checkDens = 8.0;

float ancSize = 0.03;
float mSizeMax = 0.1; //Actual mass size is a ratio of the mass to the total mass.
float l1Size = 0.005;
float l2Size = 0.005;
float curSize = 0.02;
float curThk = 0.004;

//Global Functions

//Sweeps the system paramters/initial conditions with uv coords.
//uv from 0.0 to 1.0
vec4 SweepParams(vec2 uv)
{
    float a1 = 0.0, a2 = 0.0, v1 = 0.0, v2 = 0.0;
    vec4 initState = vec4(0);

    #if(SWEEP_MODE == A1_VS_A2)
	a1 = tau*(uv.x-0.5);
	a2 = tau*(uv.y-0.5);
    #endif

    #if(SWEEP_MODE == A1A2_VS_V1)
	a1 = a2 = tau*(uv.x-0.5);
	v1 = tau*(uv.y-0.5);
    #endif

    #if(SWEEP_MODE == A1A2_VS_V2)
	a1 = a2 = tau*(uv.x-0.5);
	v2 = tau*(uv.y-0.5);
    #endif

    #if(SWEEP_MODE == A1A2_VS_M1M2)
	a1 = a2 = pi*uv.x;
	m1 = uv.y+0.1;
    m2 = (1.0-uv.y)+0.1;
    #endif

    #if(SWEEP_MODE == A1A2_VS_L1L2)
	a1 = a2 = pi*(uv.x);
	l1 = 0.3*uv.y;
    l2 = 0.3*(1.0-uv.y);
    #endif

    #if(SWEEP_MODE == CUSTOM)
	/* your code here */
    #endif

    return vec4(a1,a2,v1,v2);
}

vec2 cossin(float x)
{
	return vec2(cos(x),sin(x));   
}

float dfCircle(float d, vec2 uv)
{
    return length(uv)-d/2.0;
}

float dfRing(float d, float w, vec2 uv)
{
    return abs(length(uv)-d/2.0)-w/2.0;
}

float dfLine(vec2 start, vec2 end, float w, vec2 uv)
{
	vec2 line = end - start;
	float frac = dot(uv - start,line) / dot(line,line);
	return distance(start + line * clamp(frac, 0.0, 1.0), uv)-w/2.0;
}

#define StartCanvas(p, s) {vec2 uv=p; float ps=s; vec4 c=vec4(0); vec4 oc=vec4(0,0,0,1);
#define Color(v) oc.rgb = vec3(v);
#define Alpha(v) oc.a = float(v);
#define Circle(p,d) c=mix(c,oc,smoothstep(ps,0.,dfCircle(d,uv-(p))));
#define Ring(p,d,w) c=mix(c,oc,smoothstep(ps,0.,dfRing(d,w,uv-(p))));
#define Line(a,b,w) c=mix(c,oc,smoothstep(ps,0.,dfLine(a,b,w,uv)));
#define EndCanvas(o) o=mix(o,c,c.w);}

