{
    vec4 last = texelFetch(iChannel1,ivec2(fragCoord),0);
    vec4 cur = texelFetch(iChannel0,ivec2(fragCoord),0);
    float resetTime = last.z;
    float flipTime = last.w;
	bool reset = texelFetch(iChannel2,ivec2(0x20,0),0).x!=0.0; //Space = reset

    if(iFrame == 0 || reset)
    {
        last.xy = cur.xy;
        last.zw = vec2(0);
        resetTime = float(iFrame)*dt;
        flipTime = 3600.0; //Start with a large time where the gradient is transparent.
    }

    //Detect flip and set flipTime to the current time (relative to last reset).
    //Detects the discontinuity as the angle goes from +pi to -pi (or vise-versa).
    if(abs(last.y-cur.y) > flipThres)
    {
        flipTime = min(float(iFrame)*dt - resetTime, flipTime);
    }

    fragColor = vec4(cur.xy,resetTime,flipTime);
}

