void X (inout vec4 Q, inout vec4 a, inout vec4 aa, inout vec4 aaa, vec2 U, vec2 r) {
    vec4 b = B(U+r);
    // check b.x
    vec4 n = A(b.x);
    float ln = length(n.xy-U),
          la = length(a.xy-U);
    if (ln<la) {
    	Q.x = b.x;
        a = n;
    }
    float pn = pie(U,a.xy,n.xy),
          pa = pie(U,a.xy,aa.xy);
    if (pn<=pa){
        aa = n;
        Q.y = b.x;
    }
    float tn = tri(U,a.xy,aa.xy,n.xy),
          ta = tri(U,a.xy,aa.xy,aaa.xy);
    if (tn<ta) {
