// Maps 2D SDF to four regions, each with a different texture.
//
// Uses a "banding" function that takes a scalar value as input and splits that into
// channels in range [0,1] where the sum of all channels is 1.
//
// The sawtooth-like function `clamp(1 - abs(x-a), 0, 1)` where a in {0,1,2,3} works great
// for this purpose and can be SIMD pretty easily.
//
// The one second blip in the animation shows the different channels (alpha is black).
//
// A linear color space assumption is made for the math. 
// Shadertoy is in gamma space, so need to decode the textures to linear then encode the 
// result back.


// Maps input domain [0,3] to four channels.
// Each channel is 1.0 at the integers, and the sum of all channels is 1.
vec4 map03To4Channel(float x03) {
	return clamp(1. - abs(vec4(x03, x03 - 1., x03 - 2., x03 - 3.)), 0., 1.);
}

/* Same idea as above, but for two and three channels respectively instead of four:
    vec2 map01To2Channel(float x01) {
        return clamp(1. - abs(vec2(x01, x01 - 1.)), 0., 1.);
    }
    vec3 map02To3Channel(float x02) {
        return clamp(1. - abs(vec3(x02, x02 - 1., x02 - 2.)), 0., 1.);
    }
*/

// Approximated gamma correction.
// See https://iquilezles.org/articles/gamma for basics
// and this neat video https://www.youtube.com/watch?v=LKnqECcg6Gw
// and this cool tutorial https://www.shadertoy.com/view/lscSzl
#define GAMMA_CORRECTION 1
#define GAMMA 2.2
vec3 deGamma(vec3 c) {
    #if GAMMA_CORRECTION
    	c = pow(c,vec3(GAMMA));
    #endif
    return c;
}
vec3 gamma(vec3 c) {
    #if GAMMA_CORRECTION
    	c = pow(c,vec3(1./GAMMA));
    #endif
    return c;
}

// Sample the texture, scaling it by a factor
vec3 factorSample(sampler2D tex, vec2 uv, float factor) {
    vec3 c = texture(tex, uv).rgb;
    c = deGamma(c); // before multiplying, which needs linear space
    return c * factor;
}

// IQ's: https://iquilezles.org/articles/distfunctions2d
float sdPentagon( in vec2 p, in float r )
{
    const vec3 k = vec3(0.809016994,0.587785252,0.726542528);
    p.x = abs(p.x);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
    p -= vec2(clamp(p.x,-r*k.z,r*k.z),r);    
    return length(p)*sign(p.y);
}

// Smoothstep, but where "slope" is the derivative at value=0.5
vec4 slopeSmoothstep(vec4 value, float slope) {
    const float center = 0.5;
	return smoothstep(0.0, 1.0, slope * (value - center) + .5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*fragCoord/iResolution.y;
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float sd = sdPentagon(p, .5); // Can be any SDF
    sd = 4. * sd + 1.5; // Scale and shift
    sd = clamp(sd,0.,3.); // Clamp into domain [0,3]

    // Map distance to channels:
    vec4 channel = map03To4Channel(sd);

    // Blend channels using a step-like function
    float anim = 5.*sin(iTime*12.)+6.; // range [1,11]
    float blur = anim * fwidth(sd); // fragment width based blur (1 = smooth anti-aliasing)
    // Can be "lerp" or any other function that maintains sum-of-channels = 1
    channel = slopeSmoothstep(channel, 1. / blur);

    vec3 col;
    if (mod(floor(iTime),4.) == 0.) { // "for first second out of four"
        // Debug view
        col = channel.rgb; // throws away .a, which turns it black
    } else {
        // Multiply each texture by the associated channel
        col  = factorSample(iChannel0, uv, channel.r);
        col += factorSample(iChannel1, uv, channel.g);
        col += factorSample(iChannel2, uv, channel.b);
        col += factorSample(iChannel3, uv, channel.a);
    }

    fragColor = vec4(gamma(col),1.0);
}

// Fork of "Quadruple voronoi diagram" by michael0884. https://shadertoy.com/view/tdscRr
// 2020-03-13 12:18:57

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

float particleDistance(int id, vec2 p)
{
    return distance(getParticle(id).xy, p);
}

float gauss(float x, float r)
{
    x/=r;
    return exp(-x*x);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 col, in vec2 pos )
{
    ivec2 p = ivec2(pos);
    ivec4 sp = get(p);

    vec4 p0 = getParticle(sp.x);
   	ivec4 cp = get(ivec2(p0.xy));
    vec4 p1 = getParticle(cp.y);
    vec4 p2 = getParticle(cp.z);
    vec4 p3 = getParticle(cp.w);


    float intensity = gauss(distance(pos, p0.xy), prad);
    intensity += gauss(distance(pos, p1.xy), prad);
    intensity += gauss(distance(pos, p2.xy), prad);
    intensity += gauss(distance(pos, p3.xy), prad);
    float pw = sin(distance(pos, p0.xy)/prad);
    pw += sin(distance(pos, p1.xy)/prad);
    pw += sin(distance(pos, p2.xy)/prad);
    pw += sin(distance(pos, p3.xy)/prad);

