void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);
    if(p.x < N.x && p.y < N.y)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch1, pos);

        //neighbors
   		ivec4 cp = get(ivec2(U.xy));
   	    vec4 p1 = getParticle(cp.y);
   	    vec4 p2 = getParticle(cp.z);
   	    vec4 p3 = getParticle(cp.w);

        vec2 F = Fv(U.xy, p1.xy) +
            	 Fv(U.xy, p2.xy) +
            	 Fv(U.xy, p3.xy) +
            	 vec2(0.,0.);
        if(iMouse.z > 0.) 
        {
            float d = distance(iMouse.xy, U.xy);
            F += 3.*normalize(iMouse.xy - U.xy)/(sqrt(d)+2.);
        }

        U.zw += dt*F;
        U.zw *= 1. - 0.004*dt*length(U.zw); //cooling
        U.xy += U.zw*dt;

        //border conditions
        if(size.x - U.x < 2.) U.z = -abs(U.z);
        if(U.x < 2.) U.z = abs(U.z);
        if(size.y - U.y < 2.) U.w = -abs(U.w);
        if(U.y < 2.) U.w = abs(U.w);

        //this pixel value
        if(iFrame<1)
        {
            U.xy = size*hash22(pos);
			U.zw = 2.*(hash22(3.14159*pos) - 0.5);
        }
    }
    else discard;
}

