void sort(inout ivec4 u, inout vec4 d, int utemp, ivec2 p)
{
    vec2 pos = vec2(p);
   	float dtemp = particleDistance(utemp, pos);
    //sorting
    if(utemp != u.x && d.x > dtemp)
    {
        swap(u.x, utemp);
        swap(d.x, dtemp);
    }
    if((!any(equal(ivec2(utemp), u.xy)) && d.y > dtemp) || u.y==u.x )
    {
        swap(u.y, utemp);
        swap(d.y, dtemp);
    }
    if((!any(equal(ivec3(utemp), u.xyz)) && d.z > dtemp) || any(equal(ivec2(u.z), u.xy)))
    {
        swap(u.z, utemp);
        swap(d.z, dtemp);
    }
    if((!any(equal(ivec4(utemp), u.xyzw)) && d.w > dtemp) || any(equal(ivec3(u.w), u.xyz)))
    {
        u.w = utemp;
        d.w = dtemp;
    }
}

void upd(inout ivec4 u, inout vec4 d, ivec2 p, ivec2 dx)
{
    ivec4 t = get(p+dx);
    sort(u, d, t.x, p);
    sort(u, d, t.y, p);
    sort(u, d, t.z, p);
    sort(u, d, t.w, p);
}

void randupd(inout ivec4 u, inout vec4 d, ivec2 p )
{
    //go through the entire array sudorandomly
    int t = ((iFrame + p.x + p.y*int(iResolution.x))^32)%tot_n;
    sort(u, d, t, p);
}


void updrad(inout ivec4 u, inout vec4 d, ivec2 p, int R)
{
    int r = 1<<R;
    upd(u, d, p, ivec2(r, 0));
    upd(u, d, p, ivec2(-r, 0));
    upd(u, d, p, ivec2(0, r));
    upd(u, d, p, ivec2(0, -r));
}

void update(inout ivec4 u, inout vec4 d, ivec2 p)
{
    randupd(u, d, p);
    updrad(u, d, p, 0);
    updrad(u, d, p, 3);
    updrad(u, d, p, 5);
}

void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);

    //particle indecies 
    ivec4 sp = get(p);

   	//particle distances to this point
    vec4 dist = vec4(particleDistance(sp.x, pos), 
                     particleDistance(sp.y, pos), 
                     particleDistance(sp.z, pos), 
                     particleDistance(sp.w, pos));

    update(sp, dist, p);

    U = save(sp);
}

