    vec2 d = vec2(dot(xy,a), dot(xz,b));
    float det = determinant(mat2(xy, xz));
    return vec2(determinant(mat2(d.x, xy.y, d.y, xz.y)),determinant(mat2(xy.x, d.x, xz.x, d.y)))/det;
}

