}
float dith;
vec2 march(vec3 ro, vec3 rd, inout vec3 p, inout float t, inout bool hit){
	vec2 d = vec2(10e7);

    p = ro; t = 0.; hit = false;
    for(int i = 0; i < 230 ; i++){
    	d = map(p);
        d.x *= dith;

    	//glow += exp(-d.x*20.);
        if(d.x < 0.002){
        	hit = true;
            break;
        }

        t += d.x;
        p = ro + rd*t;
    }


    return d;
}

vec3 getNormal(vec3 p){
	vec2 t= vec2(0.001,0);
	return normalize(map(p).x - vec3(
    	map(p - t.xyy).x,
    	map(p - t.yxy).x,
    	map(p - t.yyx).x
    ));
}

