void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //adjust aspect ratio
    float ratio=iResolution.x/iResolution.y;
    uv.x*=ratio;

    //scale our grid
    float size = 200.;
    uv*=size/ratio;



    //get our cell id, starting 0 at bottom left, counting up looping each row
    float id = floor(uv.x+floor(uv.y)*size);

    //output variables and the size of our grid lines
    float o = 0., l=0., gline=size/iResolution.x;

    //if our id is in the array we calculated, set it white
    if(isPower(id))o=1.;

    //add grid lines
    uv=fract(uv);
    if(uv.x<gline||uv.y<gline) l = 1.0;

    //set the output color
    fragColor.rgb = vec3(l+o);
    //if floor(iTime)^2 == our cell id, set it red 
    if(round(pow(floor(iTime),p))==id&&l==0.)fragColor.rgb=vec3(1,0,0);
}

