float shadow(ray r){
	vec3 p0_=normalize(t.p0-light)*1000.+t.p0;
    vec3 p1_=normalize(t.p1-light)*1000.+t.p1;
    vec3 p2_=normalize(t.p2-light)*1000.+t.p2;

    vec3 n;
    float l=0.;
    float u;
    u=intersect(r,triangle(t.p0,t.p1,p0_),n);if(u!=0.){if(l==0.){l=u;}l=min(l,u);}
    u=intersect(r,triangle(t.p1,t.p2,p1_),n);if(u!=0.){if(l==0.){l=u;}l=min(l,u);}
    u=intersect(r,triangle(t.p2,t.p0,p2_),n);if(u!=0.){if(l==0.){l=u;}l=min(l,u);}

    u=intersect(r,triangle(t.p1,p1_,p0_),n);if(u!=0.){if(l==0.){l=u;}l=min(l,u);}
    u=intersect(r,triangle(t.p2,p2_,p1_),n);if(u!=0.){if(l==0.){l=u;}l=min(l,u);}
    u=intersect(r,triangle(t.p0,p0_,p2_),n);if(u!=0.){if(l==0.){l=u;}l=min(l,u);}

    return l;
}


