    //n1+=suc(r,l,light,0.2);
    r.ro+=r.rd*l;

    n1+=suc(r,1000.,light,0.2);

    n1=clamp(0.,1.,n1);




    //only need hue but does not matter, its 60+fps anyway
	res+=pow(n1*hsv2rgb(vec3(iTime,1,1)),vec3(gamma));
	l=intersect(q,t,n);

    if(l!=0.){
        vec3 p=r.ro+r.rd*l;
