// Original by FabriceNeyret2:
// https://www.shadertoy.com/view/3tKXDt

// Modified by Matthew Arcus, mla, 11/3/2020

vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return v * mix( vec3(1.0), rgb, s);
}

// Original function by Fabrice
ivec2 rationalize0(float k, float eps) {
  float m = 1e8;
  int a,_n;
  for(int n_, n=1; n < 1000; n++) {
    a = int(0.5+k*float(n));          // a/n ~ k
    float d = abs(1.0-float(a)/float(n)/k); // relative error
    if (d<m) m=d, _n=n;          // closer
    if (m <= eps ) break;           // stop at 1% rel err
  }
  return ivec2(a,_n);
}

// Find continued fraction convergent
ivec2 rationalize1(float k, float eps) {
  float k0 = k;
  int x=1, y=0, z=0, w=1;
  for (int i = 0; i < 10; i++) {
    int q = int(floor(k));
    k = 1.0/fract(k);
    int x1=z, y1=w, z1=x+q*z, w1=y+q*w;
    x=x1; y=y1; z=z1; w=w1;
    if (abs(1.0-float(w)/float(z)/k0) < eps) break;
  }
  return ivec2(w,z);
}

int gcd(int i, int j) {
 while(j != 0) {
   int i1 = j, j1 = i%j;
   i = i1; j = j1;
 }
 return i;
}

void mainImage(out vec4 outColor, vec2 fragCoord) {
  vec2 p = 2.0*fragCoord/iResolution.y;
  vec2 m = 2.0*iMouse.xy/iResolution.y;
  float eps = 5e-3, A = 20.0, B = 0.03;
  if (iMouse.z > 0.0) {
    eps = 1e-3, A = 40.0, B = 0.01;
    p -= m; p /= 10.0; p += m;
  }
  float x = p.y/p.x;
  ivec2 r = key(CHAR_X) ? rationalize0(x,eps) : rationalize1(x,eps);
  //ivec2 r = rationalize1(x,eps);
  float t = 0.1*iTime;
  vec3 col = hsv2rgb(t+float(r.y)/A,1.0,exp(-B*(float(r.y)-1.0)));
  float ds = fwidth(p.x);
  float lwidth = 0.002;
  float cwidth = 0.002;
  float cdia = 0.02;
  vec3 linecol = vec3(0.9);
  col = mix(linecol,col,smoothstep(lwidth,lwidth+ds,abs(p.y-1.0)));
  col = mix(linecol,col,smoothstep(lwidth,lwidth+ds,abs(p.x-1.0)));
  for (int i = 1; i <= 6; i++) {
    for (int j = 1; j <= 6; j++) {
      if (gcd(i,j) != 1) continue;
      col = mix(linecol,col,smoothstep(cwidth,cwidth+ds,abs(length(p-vec2(1,float(j)/float(i)))-cdia)));
      col = mix(linecol,col,smoothstep(cwidth,cwidth+ds,abs(length(p-vec2(float(j)/float(i),1))-cdia)));
    }
  }
  if (alert) col.r = 1.0;
  outColor = vec4(col,1);
}

