    //}
    //else{
    //	p3 += dot(p3, p3.yzx + 33.33+iTime/(1000.0*mag));
    //}
    return colors[int(floor(fract((p3.x + p3.y) * p3.z)*num_colors))];
}

float hash12(vec2 p,float colors[2],float mag)
{
    float num_colors = 6.0;
    //set the number of colors to be randomly generated

    vec3 p3  = fract(vec3(p.xyx) * .1031);
