    return colors[int(floor(fract((p3.x + p3.y) * p3.z)*num_colors))];
}



float magnify(vec2 fragCoord,float mag,float[6] colors){
    return hash12(floor(fragCoord/pow(3.0,mag)),colors,mag);
}

float magnify(vec2 fragCoord,float mag,float[5] colors){
    return hash12(floor(fragCoord/pow(3.0,mag)),colors,mag);
}

float magnify(vec2 fragCoord,float mag,float[2] colors){
    return hash12(floor(fragCoord/pow(3.0,mag)),colors,mag);
}

vec4 get_neighbors(vec2 fragCoord,float mag,float[5] colors){
	return vec4(magnify(fragCoord+vec2(0,1),mag,colors),
        magnify(fragCoord+vec2(1,0),mag,colors),
        magnify(fragCoord-vec2(0,1),mag,colors),
        magnify(fragCoord-vec2(1,0),mag,colors));
}

vec4 get_neighbors(vec2 fragCoord,float mag,float[2] colors){
