bool is_above(vec2 fragCoord,float mag,float color,float[6] colors){
	return magnify(fragCoord,mag,colors) == color;
}
bool is_surrounded_by(vec2 fragCoord,float mag,float color,float[6] colors){
	return magnify(fragCoord+vec2(0,1),mag,colors) == color
        && magnify(fragCoord+vec2(1,0),mag,colors) == color
        && magnify(fragCoord-vec2(0,1),mag,colors) == color
        && magnify(fragCoord-vec2(1,0),mag,colors) == color;
}

bool is_next_to(float color,vec4 neighbors){
	return (neighbors.x == color)
        || (neighbors.y == color)
        || (neighbors.z == color)
        || (neighbors.w == color);
}

