float forest_biome(in vec2 fragCoord,float mag,float zoom,float colors[6]){
    fragCoord *= zoom;
    fragCoord.x += fragCoord.y;
    fragCoord.y = fragCoord.x;
    float BLUE = colors[0];
    float GREEN = colors[1];
    float YELLOW = colors[2];
    float GRAY = colors[3];
    float FOREST_GREEN = colors[4];
    float WHITE = colors[5];

    float color1 = magnify(fragCoord,mag,colors);
    vec4 neighbors;
    while(mag > 1.0){
    neighbors = get_neighbors(fragCoord,mag,colors);
    if(color1 == BLUE && is_next_to(GREEN,neighbors)){
        color1 = GREEN;
    }

    else if(color1 == YELLOW){
        color1 = GREEN;
    }

    else if(color1 == WHITE && is_next_to(FOREST_GREEN,neighbors)){
        color1 = FOREST_GREEN;
    }
    else if(color1 == GRAY && is_next_to(GREEN,neighbors)){
        color1 = WHITE;
    }

    else if(color1 == GREEN && is_next_to(YELLOW,neighbors)){
        color1 = WHITE;
    }

    else if(color1 == GREEN && is_next_to(WHITE,neighbors)){
        color1 = FOREST_GREEN;
    }
    else if(color1 == FOREST_GREEN && is_next_to(GREEN,neighbors)){
        color1 = GREEN;
    }

    mag -= 1.0;
    }
    return color1;
}

