void mainImage( out vec4 O, vec2 u )
{
  //vec2 U = (u-.5); // /iResolution.y;
    vec2 U = u / iResolution.y;
    int _n;
    float k = U.y/U.x, d, m=1e5;
    for( int a,n=1; n < 1000; n++) {
        a = int(.5+k*float(n));          // a/n ~ k
        d = abs(1.-float(a)/float(n)/k); // relative error
        if ( d < m ) m=d, _n=n;          // closer
        if ( m <= .01 ) break;           // stop at 1% rel err
    }
    O = vec4(_n)/40.;
}

