vec4 sort(ivec2 p)
{
    //step and substep
    int pas, ste;
    for(pas = S+4, ste = iFrame%(P); ste >= pas; ste -= pas, pas--); //black magic
    ste = (pas - 1 - ste)%S;
    // pair ids
    int id0 = p.y*N + p.x;  		//this pixel id
    int id1 = id0^((1<<(ste+1))-1); //pair pixel id, by flipping first n bits, n - is the substep

	// move data sorted
    vec4 v0 = getData(iChannel0,id0);
    vec4 v1 = getData(iChannel0,id1);
    return (id0>id1) ? max(v0,v1) : min(v0,v1);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    ivec2 p = ivec2(fragCoord);

    if( max(p.x,p.y)>=N ) return;
  //if( iFrame==0 ) { fragColor = texelFetch(iChannel1,2*p,0); return; }
    if( (iFrame%(P))==0 ) { fragColor = texelFetch(iChannel1,2*p,0); return; }
  //if( iFrame>N ) return;

    fragColor = sort(p);
}

