void render_visual_pixel (out vec4 frag_color, in vec2 frag_coord) {

    /*
	  Triangular tiling on hexagonal lattice:

        01234567
      0 a--b----
      1 -------- (skip a line completely)
      2 ----c--d
      3 -------- (skip another line)


      Hexagonal dense lattice:

	    01234567
      0 a-b-c-d-
      1 -------- (skip a line completely)
      2 -e-f-g-h
      3 -------- (skip another line)


      Triangular dense dense lattice over the hexagonal:

	    01234567
      0 a-b-.-d-
      1 -------- (skip a line completely)
      2 -.-f-.-h
      3 -------- (skip another line)
	*/

    // The coordinate where vec4 frag_color is getting written.
    vec2 write_coordinate = floor((frag_coord - 0.5) / SCALE);
    // vec2 write_coordinate_for_cells = (frag_coord - 0.5) / SCALE;
    vec2 write_coordinate_for_cells = (frag_coord - 0.5) / SCALE + vec2(0.5, 0.5) / SCALE; // - 0.5 / SCALE;

    vec2 is_odd = 1.0 - floor(mod(write_coordinate, 2.0));

    vec2 tile_coord = mod(write_coordinate, vec2(8.0, 4.0));// - 0.5;

    float line_0 = 1.0 - floor(mod(write_coordinate.y / 2.0, 2.0));
    float line_2 = 1.0 - floor(mod((write_coordinate.y + 2.0) / 2.0, 2.0));

    //vec2 normalized_pixel_position = (2.0 * frag_coord / iResolution.xy) - 1.0; // -1..+1
    //float viewport_screen_ratio = iResolution.x / iResolution.y;
    //float horizontal_meters = VERTICAL_METERS * viewport_screen_ratio;

    //vec2 pixel_meters_position = vec2(
    //    normalized_pixel_position.x * horizontal_meters,
    //    normalized_pixel_position.y * VERTICAL_METERS);


    // float tile_x_isnt_4 = floor(abs(4.0 - tile_coord.x));
    // float tile_x_isnt_1_or_5 = floor(abs(1.0 - tile_coord.x));

    // float render_intensity_dense_hex = is_odd.y * (line_0 * is_odd.x + line_2 * (1.0 - is_odd.x));

    float tile_x_is_0_or_3 = 1.0 - floor(abs(0.0 - tile_coord.x)) * floor(abs(3.0 - tile_coord.x));
    float tile_x_is_4_or_7 = 1.0 - floor(abs(4.0 - tile_coord.x)) * floor(abs(7.0 - tile_coord.x));

    float render_intensity_sparse_tri = is_odd.y * (
        line_0 * tile_x_is_0_or_3 +

        line_2 * tile_x_is_4_or_7
    );


    /*
    	Cellular data storage uses the fact that on Shadertoy, buffers have the same size as screen.
     	So our tiles get 8x4=32 pixels each and each contains 4 triangles (cells).
     	These 32 pixels of buffer storage are spread over 4 cells.
		Each cell can use 8 pixels worth of data. Shadertoy provides float32 RGBA buffers,
		so each cell can have storage represented by 32 floating point numbers grouped into 8 vec4.
    */
    vec2 cell_position = vec2(
        floor(write_coordinate_for_cells.x / 2.0),
        floor(write_coordinate_for_cells.y / 4.0));

    Cell cell;
    fetch_cell(cell_position, cell);

    float render_intensity = render_intensity_sparse_tri;

    frag_color = vec4(
        render_intensity * cell.data[0].r,
        render_intensity * cell.data[0].g,
        render_intensity * cell.data[0].b,
    1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    render_visual_pixel(fragColor, vec2(fragCoord.x, iResolution.y - fragCoord.y));
}

