/*
  Hexagonal lattice can be mapped onto ordinary rectangular texture
  with odd and even rows interleaving adjacency between cells (texels).

  There is also a minimum adjacency lattice (of only three neighbor cells),
  which has the same geometry as the hexagonal, it's just missing
  cells in the centers of the hexagons.

  It is hypothesized that adjacency-3 cells are also enough to fill up
  3D space with organizing structure of cubeoctahedra.

  The tiling structure 8x4 pixels of the
  locations of the centers of triangular interconnects:

  01234567
  a--b----
  -------- (skip a line completely)
  ----c--d
  -------- (skip another line)

  Filling rendering (to show full bodies of the triangles)
  is irregular and can't be split into rectangular tiles:

  01234567
  aabb----
  a--bc--d
  ----ccdd
  e--fc--d

*/

precision highp float;
precision highp int;
precision highp sampler2D;

void fetch_cell (in vec2 cell_pos, out Cell cell) {

    vec2 pos = vec2(2.0, 4.0) * cell_pos + 0.5;

	cell.data[0] = texture(iChannel0, (pos + vec2(0.0, 0.0)) / iChannelResolution[0].xy);
    cell.data[1] = texture(iChannel0, (pos + vec2(1.0, 0.0)) / iChannelResolution[0].xy);

    cell.data[2] = texture(iChannel0, (pos + vec2(0.0, 1.0)) / iChannelResolution[0].xy);
    cell.data[3] = texture(iChannel0, (pos + vec2(1.0, 1.0)) / iChannelResolution[0].xy);

    cell.data[4] = texture(iChannel0, (pos + vec2(0.0, 2.0)) / iChannelResolution[0].xy);
    cell.data[5] = texture(iChannel0, (pos + vec2(1.0, 2.0)) / iChannelResolution[0].xy);
    cell.data[6] = texture(iChannel0, (pos + vec2(0.0, 3.0)) / iChannelResolution[0].xy);
    cell.data[7] = texture(iChannel0, (pos + vec2(1.0, 3.0)) / iChannelResolution[0].xy);
}


void buffer_a_main (out vec4 frag_color, in vec2 frag_coord) { //, in float y_for_mouse) {


    // The coordinate where vec4 frag_color is getting written.
    vec2 write_coordinate = floor(frag_coord - 0.5);
    //vec2 write_coordinate = frag_coord - 0.5;

    // 8 times per each cell this shader will be called to calculate different components.
    // We need to compute the index of the target being "rendered" to determine what one of the 8
    // components should be computed in this specific step.
    vec2 cell_position = write_coordinate / vec2(2.0, 4.0);

    vec2 is_odd = floor(mod(frag_coord, 2.0));

    float data_index_y = floor(mod(write_coordinate.y, 4.0));
    float data_index_x = is_odd.x;

    float data_index_float = data_index_y * 2.0 + data_index_x;

    float type = floor(mod(write_coordinate.x * 0.5, 4.0));
    // float type_top_left = 1.0 - floor(mod(write_coordinate.x, 8.0));

    //float is_odd_y = mod(frag_coord.y, 2.0);

    vec2 this_texel = frag_coord/iChannelResolution[0].xy;
    vec2 texel_step = 1.0 / iChannelResolution[0].xy;

    // r: gravity potential
    // g: discrete mass at the point or in the vicinity (mostly 0)
    // b: velocity of the mass transfer (dx/dt)
    // a: velocity of the mass transfer (dy/dt)


    //	Cellular data storage uses the fact that on Shadertoy, buffers have the same size as screen.
    // 	So our tiles get 8x4=32 pixels each and each contains 4 triangles (cells).
    // 	These 32 pixels of buffer storage are spread over 4 cells.
	//	Each cell can use 8 pixels worth of data. Shadertoy provides float32 RGBA buffers,
	//	so each cell can have storage represented by 32 floating point numbers grouped into 8 vec4.

    // Fetch the previous values of this cell and its 3 neighbors:
    Cell previous_this_cell_values;
    fetch_cell(cell_position, previous_this_cell_values);

    // Fetch left and right cells (exact type depends on the cell type
    Cell left_cell_values;
    fetch_cell(cell_position + vec2(-1.0, 0.0), left_cell_values);
    Cell right_cell_values;
    fetch_cell(cell_position + vec2(1.0, 0.0), right_cell_values);

    // Fetch corner cell depending on the cell type
    vec2 corner_cell_offset = vec2(
        (2.0 * floor(mod(type, 2.0))) - 1.0,
        (2.0 * floor(mod(type * 0.5, 2.0))) - 1.0);


    Cell corner_cell_values;
    fetch_cell(cell_position + corner_cell_offset, corner_cell_values);


    vec2 mouse = vec2(iMouse.x, iResolution.y - iMouse.y);
    vec2 write_coordinate_for_mouse = floor(frag_coord - 0.5) * SCALE;

    if (iMouse.z > 0.0 && length(mouse - write_coordinate_for_mouse) < 12.0 * SCALE && data_index_float < 0.5) {
    	frag_color = vec4(1.0, 1.0, 1.0, 0.0);
    } else {
        if (data_index_float < 0.5) {
            frag_color = vec4(
	        	DAMPING_RATE * (previous_this_cell_values.data[0].r + left_cell_values.data[0].r + right_cell_values.data[0].r + corner_cell_values.data[0].r),
                DAMPING_RATE * (previous_this_cell_values.data[0].g + left_cell_values.data[0].g + right_cell_values.data[0].g + corner_cell_values.data[0].g),
                DAMPING_RATE * (previous_this_cell_values.data[0].b + left_cell_values.data[0].b + right_cell_values.data[0].b + corner_cell_values.data[0].b),
                DAMPING_RATE * (previous_this_cell_values.data[0].a + left_cell_values.data[0].a + right_cell_values.data[0].a + corner_cell_values.data[0].a)
	    	);
        } else {
            frag_color = vec4(0.0, 0.0, 0.0, 0.0);
		}

    }
}


void mainImage (out vec4 fragColor, in vec2 fragCoord) {

    buffer_a_main(fragColor, vec2(fragCoord.x, fragCoord.y));
}

