void mainImage( out vec4 U, in vec2 p )
{  
    p = floor(p);
    if(max(p.x, p.y) < N)
    {
        //pair ids
        float id0 = xy2i(p);  //this pixel id
        float id1 = float(xor_n(int(id0), imod(-iFrame-1, steps))); //pair pixel id, just by flipping first n bits, n - is the step

        if(id1 < size) // if inside the array - this allows sorting arrays of non power 2 size
        {
            float val0 = texel(ch0, i2xy(id0)).x;
            float val1 = texel(ch0, i2xy(id1)).x;
            U.x = (id0 > id1) ? max(val0, val1) : min(val0, val1); //swap sorted
        }
        else
        {
            U = texel(ch0, p);
        }

        if(imod(iFrame, steps*stepsf) == 0)
        {
            U.x = hash12(p);
        }
    }
    else discard;
}

