#define texel(a, p) texelFetch(a, ivec2(p), 0)
#define ch0 iChannel0
#define N 300.

float size = N*N;
float steps = ceil(log2(N*N));
float stepsf = floor(log2(N*N));

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

//mappings
vec2 i2xy(float id)
{
    return vec2(mod(id, N), floor(id/N));
}

float xy2i(vec2 xy)
{
    return xy.x + xy.y*N;
}

//flip lowest n bits
int xor_n(int x, int n)
{
    return x ^ ( (1<<(n+1)) - 1 );
}

int imod(int a, float b)
{
    return a - int(b*floor(float(a)/b));
}

