// Fork of "Bitonic sorting network" by michael0884. https://shadertoy.com/view/ttVXDV
// 2020-03-08 15:43:17

//get particle id from id array
vec2 getID(float id, float LI)
{
    id = clamp(id, 0., N*N-1.);
    float y = floor(id/N);
    float x = id - N*y;
    return texel(ch1, vec2(x+LI*N,y)).xy;
}


//random binary array id search
vec2 search(int zid, float LI)
{
    float L = 0., R = N*N-1.;
    vec4 p;
    float i = 0.;
    while(L!=R && i < log2(N))
    {
        i++;
    	float lid = floor((L+R)/2.);
        float zid0 = getID(lid, LI).y; 
        float k = 0.5;
        if(float(zid) < zid0)
        {
            R = (1.-k)*lid+R*k;
        }
        else
        {
            L = (1.-k)*lid+L*k;
        }
    }
    return vec2(L,R);
}

float gauss(float x, float r)
{
    x/=r;
    return exp(-x*x);
}

#define R 8.

void mainImage( out vec4 fragColor, in vec2 p )
{
    vec2 pn = floor(1.*p);
    p = floor(vec2(1.,1.)*p);
    if(pn.x < LN*N && pn.y < N)
    {
    	vec2 a = texel(ch1, pn).xy;
        fragColor.xyz = jet_range(a.y, 0., 3e9);
    }
    else
    {
        int zid = xy2z(p);
        //render points using last LN particle sorts
        for(float li = 0.; li < LN; li++)
        {
            vec2 lid = search(zid, li);
            fragColor.xyz = vec3(0);
            for(float i = lid.x;i <= lid.y; i++)
            {
                vec4 a0 = getParticle(getID(i, li).x);
                fragColor.xyz += vec3(gauss(length(p - a0.xy), 2.));
            }
        }
    }
}

