vec2 nz(vec2 a)
{
    return (a.x == 0. && a.y == 0.)?vec2(0.):normalize(a);
}

vec2 cF(vec2 p)
{
    vec4 a = getParticle(p, vec2(0));
    vec2 F = vec2(0.);

    int zid = xy2z(a.xy);
    vec2 lid = search(zid, 0.);

    for(float i = lid.x;i <= lid.y; i++)
    {
        vec4 a0 = getParticle(getID(i, 0.).x);
        float d = distance(a0.xy, a.xy);
        F += nz(a0.xy - a.xy)*abs(d - D)*exp(-0.08*d);
    }
    return F;

}

void mainImage( out vec4 U, in vec2 pos )
{
    if(pos.x < N && pos.y < N)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch0, pos);

        vec2 F = -0.1*cF(pos);
        if(iMouse.z > 0.) 
        {
            float d = distance(iMouse.xy, U.xy);
            F -= 5.*normalize(iMouse.xy - U.xy)/(sqrt(d)+2.);
        }

        U.zw += dt*F;
        U.zw *= 0.9;
        U.xy += U.zw*dt;


        //this pixel value
