float getID(float id)
{
    id = clamp(id, 0., N*N-1.);
    float y = floor(id/N);
    float x = id - N*y;
    return texel(ch1, vec2(x,y)).x;
}

vec2 sort(float id, float pid)
{

    float p0_id = getID(id);
    float p1_id = getID(pid);

    vec4 val0 = getParticle(p0_id);
    vec4 val1 = getParticle(p1_id);

    //z-curve indecies
    int zcurve0 = xy2z(val0.xy);
    int zcurve1 = xy2z(val1.xy);

    vec2 cmin = (zcurve0<zcurve1) ? vec2(p0_id, zcurve0): vec2(p1_id, zcurve1);
    vec2 cmax = (zcurve0<zcurve1) ? vec2(p1_id, zcurve1): vec2(p0_id, zcurve0);

    return (zcurve0==zcurve1)?vec2(p0_id, zcurve0):((pid > id) ? cmin : cmax); //swap sorted
}

//flip n bits
float XORN(float x, float n)
{
    int X = int(x);
    int pow2m1 = (1<<int(n+1.)) - 1;
    return float(X^(pow2m1));
}

int imod(int x, int y)
{
    return x - y * int(floor(float(x)/float(y)));
}

