    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch1, pos);

        float id = pos.x + pos.y*N;  

        int maxorder = int(ceil(log2(N*N)));

        int frame = iFrame;
        float order = float(imod(frame, maxorder));
        float oe = float(imod(frame, 2));

        //pair id
        float pid = XORN(id+oe, order)-oe; 

        if(pid < N*N) //if inside the array
        {
            U.xy = sort(id, pid); //swap sorted
        }

        if(iFrame < 1)
        {
            U.x = id;
        }
    }
    else discard;
}

