const float PI = 3.1415926;
const float TAU = PI * 2.0;
const float E = 0.01;

struct Ray
{
    vec3 pos;
    vec3 dir;
};

mat2 rotate2D(float rad)
{
    float c = cos(rad);
    float s = sin(rad);
    return mat2(c, s, -s, c);
}

vec2 de(vec3 p)
{
    float d = 100.0;
    float a = 0.0;

    p.yz *= rotate2D(PI / 5.0);
    p.y -= 0.5;

    // reaction
    vec3 reaction = vec3(cos(iTime), 0.0, sin(iTime)) * 3.0;
    p += exp(-length(reaction - p) * 1.0) * normalize(reaction - p);

    // cables
    float r = atan(p.z, p.x) * 3.0;
    const int ite = 50;
    for (int i = 0; i < ite; i++)
    {
        r += 0.5 / float(ite) * TAU;
        float s = 0.5 + sin(float(i) * 1.618 * TAU) * 0.25;
        s += sin(iTime + float(i)) * 0.1;
        vec2 q = vec2(length(p.xz) + cos(r) * s - 3.0, p.y + sin(r) * s);
        float dd = length(q) - 0.035;
        a = dd < d ? float(i) : a;
    	d = min(d, dd);
    }

    // sphere
    float dd = length(p - reaction) - 0.1;
    a = dd < d ? 0.0 : a;
    d = min(d, dd);

    return vec2(d, a);
}

void trace(Ray ray, inout vec3 color, float md)
{
    float ad = 0.0;
    for (float i = 1.0; i > 0.0; i -= 1.0 / 128.0)
    {
        vec2 o = de(ray.pos);
        if (o.x < E)
        {
            color = mix(vec3(0.1, 0.1, 0.5), vec3(0.0, 0.0, 1.0), fract(o.y * 1.618));
            color = mix(vec3(1.0, 1.0, 1.0), color, step(0.05, fract(o.y * 1.618)));
            color = mix(vec3(0.175, 0.1, 0.1), color, step(0.35, fract(o.y * 1.618 + 0.9)));
            color *= exp(-(1.0 - i) * 15.0);
            return;
        }

        o.x *= 0.6;
        ray.pos += ray.dir * o.x;
        ad += o.x;
        if (ad > md)
        {
            break;
        }
    }

    color = mix(vec3(0.0), vec3(1.0), ray.dir.y * ray.dir.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 color = vec3(0.0);

    vec3 view = vec3(0.0, 0.0, 10.0);
    vec3 at = normalize(vec3(0.0, 0.0, 0.0) - view);
    vec3 right = normalize(cross(at, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, at);
    float focallength = 3.0;

    Ray ray;
    ray.pos = view;
    ray.dir = normalize(right * p.x + up * p.y + at * focallength);

    trace(ray, color, 20.0);

    color = pow(color, vec3(0.454545));
    fragColor = vec4(color, 1.0);
}

int method; // global variable only for this toy;
// my actual codebase uses a separate function 
// for each operator and the inverse thereof.

    //c = max(0., c); // pow doesn't appreciate negatives but here we never have any

// my curve can be tuned for various gamma approximations etc.
// but would need to adjust the inverse operator to match.
// I figure being close to ACES is probably not a bad thing.

// the ACES filmic tonemap operator explained here:
//   http://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve
// main ACES site on github, for further reference:
//   http://github.com/ampas/aces-dev

// anyway it's not precisely ACES+sRGB, it wiggles around it like 3 times,
// has in fact a little deeper toe as you can see a slight band between 
// the red and green swatches in this toy.  But that's partially because
// this one isn't tuned to the precise scale I had in the other toy
// where I was actually trying to match it as exactly as possible.
// point is, I'm sure it can be tuned to match ACES a little better 
// if you care enough to run some optimizer on it.

// I mostly tend to care about low-exposure scenes, so I'm probably
// not the most knowledgeable tone mapping guru around,
// so if anyone has any suggestions, I'm all ears.

// linear to sRGB
float toneMap(float c)
{
    switch (method) {
  	  case 0: // to sRGB gamma via filmic-like Reinhard-esque combination approximation
	    c = c / (c + .1667); // actually cancelled out the gamma correction!  Probably cheaper than even sRGB approx by itself.
		break; 	// considering I can't tell the difference, using full ACES+sRGB together seems quite wasteful computationally.
  	  case 1: // ACES+sRGB, which I approximated above
	    c = ((c*(2.51*c+.03))/(c*(2.43*c+.59)+.14));
	    c = pow(c, 1./2.2); // to sRGBish gamut
	    break;
  	  default: // sRGB approx by itself
	    c = pow(c, 1./2.2);
        break;
    }
    return c;
}

// sRGB to linear
float unToneMap(float c)
{
    switch (method) {
  	  case 0: // via inverse filmic-like Reinhard-esque combination approximation
        c = c/(6.-6.*c);
		break;
      case 1: // inverse ACES+sRGB
        c = pow(c, 2.2); // from sRGBish gamut
        c = -(sqrt((-10127.*c+13702.)*c+9.)+59.*c-3.) / (486.*c-502.); // inverse ACES according to Maxima
	    break;
      default: // just approx un-sRGB
        c = pow(c, 2.2);
        break;
    }
    return c;
}

// combined exposure, tone map, gamma correction
vec3 toneMap(vec3 crgb, float exposure) // exposure = white point
{
    vec4 c = vec4(crgb, exposure);
    for (int i = 4; i-- > 0; ) c[i] = toneMap(c[i]);
    // must compute the tonemap operator of the exposure level, although optimizes out at compile time, 
    // do it all in vec4 and then divide by alpha.
    return c.rgb / c.a;
}

// for completeness - sRGB gamma tonemapped color back to linear - inverse tone map
vec3 unToneMap(vec3 crgb, float exposure)
{
    vec3 c = crgb * toneMap(exposure);
    for (int i = 3; i-- > 0; ) c[i] = unToneMap(c[i]);
    return c;
}

vec3 dither(vec3 c, vec2 p)
{
    float x; // noise
    x = fract(iTime*0. + 23456.7*sin(dot(p, vec2(12.93,8.57)))); // from ALU hash
    x = 2. * x - 1.; // signed
    c += .75 * exp2(-8.) * x; // tuned for 8 bits per channel
    return c;
}

void mainImage(out vec4 o, vec2 p)
{
    if (iChannelResolution[0].x == 0.) { o = vec4(1,0,0,1); return; } // hopefully fix the toy warning by handling texture not yet loaded!?
    vec2 uv = p / iResolution.xy;
    vec4 t = texture(iChannel0, uv);
    t.rgb = pow(t.rgb, vec3(2.2)); //t.rgb*t.rgb; // from sRGB
    vec3 c = t.rgb;
    const float vary = 3.; //.1; //1.; //24.; //.5; //
    const float lowest = .1; //1.; //.25; //.5; //
    float expo = (sin(iTime*1.2) + 1.) * vary + lowest;
    // blow out! or try, anyway.  or dim.
    // tonemap can handle either way ok
    // but it does affect the color balance
    c *= expo;
    method = int((uv.x + iTime * .01) * 6.) % 3; //0; //1; //
  #if 0
    // roundtrip debugging
    if ((iFrame & 16) != 0) { // fast flicker means botched.  no flicker means success!
    c = toneMap(c, expo);
    c = unToneMap(c, expo);
    }
  #endif
    c = toneMap(c, expo);
    // color code method at bottom (red = my approx, green = ACES+sRGB, blue = just sRGB)
    if (abs(uv.y-.5)>.495) c = pow(max(vec3(0),cos(float(method) * 2. - vec3(0,2,4))), vec3(.45));
	c = dither(c, p);
	o = vec4(c, 1);
}


