const float FAR_PLANE = 20.0;
const float SURF_HIT = 0.01;

float sphere(vec3 p, float radius) { 
    return length(p) - radius;
}

float plane(vec3 p, float height) {
    return p.y - height;
}

float map(vec3 p) {
    float ball = sphere(p, 1.0);
    float floorPlane = plane(p, -1.0);
    return min(ball, floorPlane);
}

vec3 mapNormal(vec3 p) {
    vec2 e = vec2(SURF_HIT, 0);
    float d = map(p);

    return normalize(vec3(
        d - map(p - e.xyy),
        d - map(p - e.yxy),
        d - map(p - e.yyx)
    ));
}

float trace(vec3 ro, vec3 rd, int maxIter, float maxD) {
    float d = 0.0;
    for (int i = 0; i < maxIter && d <= maxD; i++) {
        vec3 p = ro + rd * d;
        float s = map(p);
        d += s;
        if (s < SURF_HIT) break;
    }
    return d;
}

vec3 render(vec3 ro, vec3 rd) {
    float d = trace(ro, rd, 128, FAR_PLANE);

    if (d >= FAR_PLANE) { return vec3(0); }

    vec3 p = ro + rd*d;
    vec3 n = mapNormal(p);
    vec3 l = normalize(vec3(1));

    float lambert = max(0.0, dot(l, n));

    return vec3(lambert);
}


