float Kisses(float a, float b, float k)
{
    float h = clamp((b-a)/k + .5, 0., 1.);
    return mix(a,b,h) + h * (1. - h)* k * .5;
}


vec2 Hugs(float blur, vec2 uv)
{
 	uv.x *= .7 + blur*.3;

    float radius = .25;

    float shape = abs(uv.x);

    uv.y -= Kisses(sqrt(shape) * .35, blur * radius, .1);

    uv.y += .1 + blur * radius * .5;

    return uv;
}

vec3 TheShapeOfMyHeart(float blur, vec2 uv)
