//random binary array id search
vec2 search(int zid, float LI)
{
    float L = 0., R = N*N-1.;
    vec4 p;
    float i = 0.;
    while(L!=R && i < log2(N))
    {
        i++;
    	float lid = floor((L+R)/2.);
        float zid0 = getID(lid, LI).y; 
        float k = 0.5;
        if(float(zid) < zid0)
        {
            R = (1.-k)*lid+R*k;
        }
        else
        {
            L = (1.-k)*lid+L*k;
        }
    }
    return vec2(L,R);
}

float gauss(float x, float r)
{
    x/=r;
    return exp(-x*x);
}

