bool pixelGradient(in vec2 uv) {
	uv = floor(uv * 40.);
    float percentage = floor(uv.x /40. * 10.);
      int p = int(percentage)/2,
          n = 8 >>  min(p,4-p), 
          q = 4 - 2*min(p,4-p),
          i = int(uv.x + uv.y) % n, 
          x = int(uv.x) % q; 
	return
        p < 1 ?    i==0 && x > 2
      : p < 2 ?    i==0 && x > 0
      : p < 3 ?    i==0
      : p < 4 ? !( i==0 && x > 0 )
      : p < 5 ? !( i==0 && x > 2 )
      : true;

}


