{
    _time = iTime / 2.0;
    vec3 tot = vec3(0.0);

    //Camera
    float nearP = 1.0;
    float roll = 0.0;
    vec3 ta = vec3(0.0);
    vec3 ro = ta + vec3(0.0, 0.2, -1.0);

#if AA > 1
    for(int i =0; i < AA; ++i)
    for(int j =0; j < AA; ++j)
    {
        vec2 offset = 0.5 - vec2(i, j) / float(AA);
