//sorting network

//get particle id from id array
float getID(float id)
{
    id = clamp(id, 0., N*N-1.);
    float y = floor(id/N);
    float x = id - N*y;
    return texel(ch1, vec2(x,y)).x;
}

#define slowdown 10.
void mainImage( out vec4 U, in vec2 pos )
{  
    if(pos.x < N && pos.y < N)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch1, pos);

        float id = pos.x + pos.y*N;  

        if(mod(float(iFrame), slowdown) == 0.)
        {
            //initial parameters for bitonic sort step
            float frame = floor(float(iFrame)/slowdown);
            float Nlog2 = ceil(log2(N*N));
            int cstep = int(mod(frame, Nlog2*Nlog2)); //bitonic sort step
            int rank;
            for(rank = 0; rank<cstep ; rank++)
            {
                cstep -= rank+1;
            }
            float stepno = float(1<<(rank+1));
            float offset = float(1<<(rank-cstep));
            float stage  = 2.*offset;

            //bitonic sort
            float csign = (mod(id, stage)< offset) ? 1. : -1.;
            float cdir  = (mod(floor(id/stepno),2.)<=0.5) ? 1. : -1.; 

			float adr1d = csign*offset + id;

            float p0_id = U.x;
            float p1_id = getID(adr1d);

            vec4 val0 = getParticle(p0_id);
            vec4 val1 = getParticle(p1_id);

            float cmin = (val0.z<val1.z) ? p0_id: p1_id;
            float cmax = (val0.z<val1.z) ? p1_id: p0_id;

            U.x = (csign==cdir) ? cmin : cmax;
        }


        if(iFrame < 1)
        {
            U.x = id;
        }
    }
    else discard;
}

